INSERT INTO `news` (`id`, `title`, `thumbnail`, `shortdescription`, `content`, `cate_id`, `flag`, `view`, `insert_time`, `user_id`, `status`) VALUES
(1, 'Liverpool đả bại Real', '/uploads/sport1752668281846600.jpg', 'Liverpool hủy diệt Real tại sân khách.', '<p>Đả bại Real tại s&acirc;n kh&aacute;ch, Liverpool ch&iacute;nh thức gi&agrave;nh chiếc v&eacute; v&agrave;o v&ograve;ng 16 đội Champion league</p>\r\n', 9, 2, 0, '2023-04-23 03:15:28', 1, 0),
(3, 'Thua muối mặt trước Bayern, Barce chia tay giải đấu.', '', 'Barce chính thức chia tay Champion League sau khi để thua Bayern Munich 0-3.', '<p>Đả bại Barce, Bayern nghiễm nhi&ecirc;n chiếm ng&ocirc;i đầu bảng.</p>\r\n', 9, 0, 0, '2023-03-30 08:50:22', 2, 1),
(5, 'Đêm C1, PSG và Tottenham dắt tay nhau ra về', '/uploads/tiger.jpg', 'PSG thảm bại tại sân Bayern, Tottenham không thể xuyên thủng hàng phòng ngự của AC Milan', '', 9, 2, 0, '2023-02-26 15:04:28', 3, 1),
(6, 'Người đầu tiên trở thành vua tiếng việt', '/uploads/hamster.jpg', 'Thắng sát sao, người đầu tiên trở thành vua tiếng việt', '', 39, 2, 1, '2023-02-26 15:29:36', 4, 0),
(9, 'Liverpool hủy diệt Manu', '/uploads/sport2752769318655500.jpg', 'Hủy diệt Manu ngay tại thánh địa Anfield', '<p>Hiệp 1 thế trận giằng co, liverpool chỉ vươn l&ecirc;n với t&igrave;nh huống s&uacute;t như kẻ chỉ của Cody Gakpo.</p>\r\n\r\n<p>Sang hiệp 2 thế trận ho&agrave;n to&agrave;n đổi chiều. Chỉ chưa đầy 5p sau khi bắt đầu hiệp 2, Liver c&oacute; b&agrave;n thằng thứ 2 do c&ocirc;ng của Darwin Nunez.&nbsp;</p>\r\n\r\n<p>H&agrave;ng ph&ograve;ng ngự Mu chơi như mơ ngủ với kết quả thua chung cuộc 7-0.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://localhost:8089/userfiles/files/hinh-nen-hoa-anh-dao-27.jpg\" style=\"height:70px; width:100px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n', 9, 1, 13, '2023-04-23 03:17:09', 5, 0),
(10, 'U23 Việt Nam thua đậm', '/uploads/sport752864993396400.png', 'Thua bạc nhược ở doha cup, U23 Việt Nam tiếp tục làm người hâm mộ thất vọng', '<p>U23 Việt Nam bước v&agrave;o lượt trận thứ 2 ở Doha Cup 2023 với nhiều sự thay đổi trong đội h&igrave;nh xuất ph&aacute;t. Thủ th&agrave;nh Đo&agrave;n Huy Ho&agrave;ng, hậu vệ Trần Quang Thịnh, Trần Văn Thắng v&agrave; tiền vệ L&ecirc; Quốc Nhật Nam. Tuy nhi&ecirc;n những sự thay đổi của HLV Troussier dường như kh&ocirc;ng c&oacute; nhiều t&aacute;c dụng.</p>\r\n\r\n<p>Kh&ocirc;ng qu&aacute; bất ngờ khi UAE l&agrave; đội l&agrave;m chủ thế trận. Đ&uacute;ng như dự đo&aacute;n, ng&ocirc;i sao s&aacute;ng nhất b&ecirc;n ph&iacute;a đội bạn l&agrave; Ahmed Fawzi Abdulla li&ecirc;n tục l&agrave;m khổ h&agrave;ng ph&ograve;ng ngự của U23 Việt Nam với những pha đi b&oacute;ng cực kỳ kh&eacute;o l&eacute;o v&agrave; những c&uacute; s&uacute;t xa. Rất may thủ m&ocirc;n Huy Ho&agrave;ng đ&atilde; chơi tốt.</p>\r\n\r\n<p>U23 Việt Nam cũng c&oacute; 1 t&igrave;nh huống đ&aacute;ng ch&uacute; &yacute; trong hiệp 1 khi Văn Đ&ocirc; căng ngang để Văn Trường dứt điểm cận th&agrave;nh nhưng b&oacute;ng đi tr&uacute;ng người hậu vệ UAE. Tiền vệ của U23 Việt Nam cho rằng đối thủ chơi b&oacute;ng bằng tay nhưng trọng t&agrave;i n&oacute;i kh&ocirc;ng. Hiệp 1 kết th&uacute;c với tỷ số h&ograve;a 0-0.</p>\r\n\r\n<p>Tuy nhi&ecirc;n điều g&igrave; đến cũng đ&atilde; đến khi U23 Việt Nam nhận b&agrave;n thua đầu ti&ecirc;n ở ph&uacute;t thứ 53. Điểm yếu thể h&igrave;nh đ&atilde; được UAE tận dụng rất tốt với pha bật cao đ&aacute;nh đầu dễ d&agrave;ng của Ysaer Hassan. Để rồi đến ph&uacute;t 65, tỷ số đ&atilde; được n&acirc;ng l&ecirc;n 2-0 sau c&uacute; cứa l&ograve;ng si&ecirc;u phẩm của đội trưởng UAE.</p>\r\n\r\n<p>HLV Troussier sau đ&oacute; c&oacute; h&agrave;ng loạt sự thay đổi người khi Quốc Việt v&agrave; Văn Khang được tung v&agrave;o s&acirc;n. Văn Khang lập tức tạo điểm nhấn với c&uacute; v&ocirc; l&ecirc; ở r&igrave;a v&ograve;ng cấm nhưng b&oacute;ng đi hơi cao so với khung th&agrave;nh. Tuy nhi&ecirc;n khi những t&iacute;n hiệu tốt vừa mới đến th&igrave; U23 Việt Nam cũng phải nhận b&agrave;n thua thứ 3 ở ph&uacute;t thứ 83 cũng từ 1 t&igrave;nh huống đ&aacute;nh đầu của c&aacute;c tiền đạo cao to b&ecirc;n ph&iacute;a UAE.</p>\r\n', 8, 4, 0, '2023-04-23 03:18:45', 1, 0),
(11, 'Trương Đình Hoàng bảo vệ thành công đai WBA châu Á', '/uploads/sport4753010428473400.jpg', 'Dù vấp phải sự kháng cự quyết liệt từ Baek Dae Hyun, Trương Đình Hoàng vẫn cho thấy bản lĩnh và đẳng cấp, để giành chiến thắng điểm trong sự kiện quyền anh tại TP.HCM tối 25/3.', '<p>Xuy&ecirc;n suốt c&aacute;c hiệp đấu, hai v&otilde; sĩ t&iacute;ch cực trao đổi đ&ograve;n tốc độ chớp nho&aacute;ng với những c&uacute; đấm m&oacute;c uy lực v&agrave;o mặt. Cả Đ&igrave;nh Ho&agrave;ng v&agrave; Baek Dae Hyun đều thể hiện sự tự tin, đầy kh&ocirc;n ngoan trong c&aacute;ch thi triển chiến thuật v&agrave; điều chỉnh nhịp độ thi đấu để khắc chế đối phương.</p>\r\n\r\n<p>Bắt đầu từ hiệp 7 đến cuối, Đ&igrave;nh Ho&agrave;ng dần chiếm ưu thế bằng những tổ hợp đấm ch&iacute;nh x&aacute;c. &quot;Nam vương boxing&quot; Việt Nam cho thấy kinh nghiệm vượt trội khi ph&acirc;n phối thể lực v&agrave; chọn thời điểm tung đ&ograve;n hợp l&yacute; khiến Baek Dae Hyun c&oacute; nhiều khoảnh khắc cho&aacute;ng v&aacute;ng.</p>\r\n\r\n<p>Chung cuộc, Đ&igrave;nh Ho&agrave;ng gi&agrave;nh chiến thắng điểm, bảo vệ th&agrave;nh c&ocirc;ng đai v&ocirc; địch WBA ch&acirc;u &Aacute; hạng c&acirc;n super-middleweight.</p>\r\n\r\n<p>Sau chiến thắng, Đ&igrave;nh Ho&agrave;ng chia sẻ: &quot;T&ocirc;i cảm ơn mọi người khi đến đ&acirc;y cổ vũ cho c&aacute;c v&otilde; sĩ Việt Nam. Đối thủ người H&agrave;n Quốc rất trẻ, sung sức. T&ocirc;i v&agrave; ban huấn luyện nghi&ecirc;n cứu rất kỹ đối thủ. Thực sự cảm ơn &ecirc;-k&iacute;p huấn luyện vi&ecirc;n đ&atilde; gi&uacute;p t&ocirc;i để c&oacute; kết quả n&agrave;y&quot;.</p>\r\n\r\n<p>Đ&igrave;nh Ho&agrave;ng năm nay 32 tuổi, từng l&agrave; v&otilde; sĩ quyền anh đầu ti&ecirc;n của Việt Nam gi&agrave;nh đai WBA ch&acirc;u &Aacute; v&agrave; WBA Đ&ocirc;ng &Aacute;. Năm 2019, v&otilde; sĩ qu&ecirc; Đắk Lắk từng đ&aacute;nh bại cao thủ H&agrave;n Quốc Gyu Hyun Lee sau 10 hiệp ở H&agrave; Nội. Đ&acirc;y l&agrave; trận đấu gi&uacute;p tay đấm của Việt Nam lấy đai WBC Đ&ocirc;ng &Aacute;.</p>\r\n\r\n<p>Một năm sau, v&otilde; sĩ sinh năm 1990 c&oacute; chiến thắng thuyết phục trước Yuttana Wongda ở trận tranh đai WBA ch&acirc;u &Aacute;.</p>\r\n', 10, 4, 0, '2023-04-23 03:21:10', 2, 0),
(12, 'Đại chiến viễn thông bùng nổ T1 thắng khó KT', '/uploads/sport3752946946673000.jpg', 'Loạt trận bo5 nghẹt thở, T1 khó khăn trong việc chiến thắng KT', '<p>V&aacute;n 1 diễn ra với t&igrave;nh thế &aacute;p đảo tới nghẹt thở từ T1 khiến b&igrave;nh luận vi&ecirc;n Ho&agrave;ng Lu&acirc;n n&oacute;i rằng : &quot; KT kh&ocirc;ng l&agrave;m g&igrave; sai cả, chỉ l&agrave; họ ra đường farm l&iacute;nh v&agrave; thua th&ocirc;i&quot;. C&ugrave;ng với đ&oacute; ta thấy &quot; Việc sai lầm duy nhất của KT l&agrave; gặp T1&quot;.&nbsp;</p>\r\n\r\n<p>Nhưng sang v&aacute;n 2 mọi thứ đ&atilde; thay đổi, việc được pick k&egrave;o sau v&agrave; xanh sớm đ&atilde; khiến Kiin trở th&agrave;nh 1 con jax qu&aacute;i vật thật sự, d&ugrave; với 2 t&igrave;nh huống cướp 3r nhưng T1 vẫn kh&ocirc;ng thể n&iacute;u giữ v&aacute;n đấu. tỉ số c&acirc;n bằng 1-1.</p>\r\n\r\n<p>Sang v&aacute;n 3 vẫn l&agrave; con Jax đấy nhưng lần n&agrave;y Zeus kh&ocirc;ng cầm gnar nữa m&agrave; cầm Ksante. Với những pha sử l&iacute; thần sầu của Zeus th&igrave; Kiin trận n&agrave;y mất t&iacute;ch kh&ocirc;ng thấy tăm hơi đ&acirc;u. C&aacute;nh dưới cả 2 b&ecirc;n đều c&oacute; những sai lầm nhưng người l&agrave;m tốt hơn l&agrave; T1. 2-1 cho T1.</p>\r\n\r\n<p>V&aacute;n 4 diễn ra với h&agrave;ng loạt t&igrave;nh huống gank sớm b&ecirc;n KT. V&agrave; mạng được dồn cho con Zeri của Aiming. Vẫn cặp đ&ocirc;i cũ Jax với TF. Nhưng lần n&agrave;y việc cầm 1 con galio khiến Faker kh&ocirc;ng c&oacute; nhiều t&aacute;c dụng th&ecirc;m với việc thiếu s&aacute;t thương n&ecirc;n gần như việc duy nhất faker l&agrave;m l&agrave; g&oacute;p khống chế. Với 1 con jax thần, 1 con TF v&agrave; Zeri xanh sớm th&igrave; KT nhanh ch&oacute;ng kết th&uacute;c v&aacute;n đấu v&agrave; c&acirc;n bằng tỉ số 2-2.</p>\r\n\r\n<p>V&aacute;n đấu cuối c&ugrave;ng diễn ra đầy căng thẳng. Bằng những t&igrave;nh huống di chuyển v&agrave; gank đầu trận KT đ&atilde; khiến con Jayce của Kiin c&oacute; KD 4/0 tưởng chừng như việc snowball kết th&uacute;c kh&ocirc;ng c&oacute; việc g&igrave; kh&oacute; th&igrave; T1 vẫn cứ l&agrave; T1. Với c&aacute;ch biệt thua 7k tiền nhưng T1 vẫn combat thắng KT với t&igrave;nh huống &eacute;p baron. Faker l&agrave;m tốt pha makeplay khiến Jayce của Kiin ra đi m&agrave; ch&acirc;n kh&ocirc;ng chạm đất. Sau những pha d&agrave;nh dật cướp rồng v&agrave; rush Baron. T1 chiến thắng tỉ số 3-2. Kết th&uacute;c 1 cuộc Bo5 đại chiến viễn th&ocirc;ng b&ugrave;ng nổ.</p>\r\n\r\n<p><img alt=\"\" src=\"http://localhost:8089/userfiles/files/T1.png\" style=\"height:141px; width:357px\" /></p>\r\n', 11, 4, 2, '2023-04-23 03:20:07', 3, 0),
(13, 'HLE với GENG đội nào sẽ gặp T1 tại chung kết', '/uploads/rabbit920644519553500.jpg', 'Dự đoán khó lường cặp trận GENG với HLE', '<p>Trận đấu kh&oacute; lường giữa 2 đại diện mạnh của LCK. Dự đo&aacute;n 3-2 cho GENG</p>\r\n', 11, 4, 3, '2023-03-26 03:37:04', 4, 0),
(14, 'U23 Việt Nam toàn bại tại doha cup', '/uploads/tiger1182423456461900.jpg', 'U23 thể hiện bộ mặt bạc nhược tại doha cup', '<p>abcde</p>\r\n', 8, 2, 0, '2023-03-30 10:00:31', 5, 0),
(15, 'Bóng đá trẻ việt nam lụi bại', '/uploads/T11407308719098100.png', 'Bóng đá trẻ việt nam đang dần thua kém trong khu vực', '<p>OMG&nbsp;<img alt=\"\" src=\"http://localhost:8089/userfiles/files/squirrel.jpg\" style=\"height:188px; width:268px\" /></p>\r\n', 8, 5, 1, '2023-03-31 18:48:01', 1, 0),
(16, 'Những vụ án rúng động lịch sử qua góc nhìn của nhà văn', '/uploads/nhavan1743756530854100.jpg', '“Thảm kịch vĩ nhân”, “Công chúa Đồng Xuân”, “Thiên thu huyết lệ” là những tiểu thuyết lịch sử có tính chất chiêu tuyết (rửa sạch oan ức) cho những án oan ngút trời trong lịch sử.', '<p>Trong thời kỳ qu&acirc;n chủ của Việt Nam, c&oacute; kh&ocirc;ng &iacute;t vị c&ocirc;ng thần c&oacute; nhiều c&ocirc;ng trạng với d&acirc;n tộc, đất nước đ&atilde; trở th&agrave;nh nạn nh&acirc;n của những vụ &aacute;n thảm khốc. C&oacute; thể kể đến vụ &aacute;n Th&aacute;i sư h&oacute;a hổ, hay c&ograve;n gọi l&agrave; vụ &aacute;n hồ D&acirc;m Đ&agrave;m (Hồ T&acirc;y) diễn ra v&agrave;o năm 1096 v&agrave; nạn nh&acirc;n l&agrave; Th&aacute;i sư L&ecirc; Văn Thịnh - người được coi l&agrave; vị trạng nguy&ecirc;n khai khoa mở đầu cho học giới nước nh&agrave; từ thế kỷ XI.</p>\r\n\r\n<p>Hay như vụ &aacute;n Lệ Chi Vi&ecirc;n diễn ra v&agrave;o năm 1442 với &aacute;n oan ng&uacute;t trời &ldquo;giết vua&rdquo;, c&ugrave;ng bản &aacute;n thảm khốc &ldquo;tru di tam tộc&rdquo; nhằm v&agrave;o anh h&ugrave;ng d&acirc;n tộc, danh nh&acirc;n văn h&oacute;a Nguyễn Tr&atilde;i c&ugrave;ng vợ &ocirc;ng l&agrave; Lễ nghi học sĩ Nguyễn Thị Lộ&hellip; B&ecirc;n cạnh đ&oacute; c&ograve;n c&oacute; những vụ &aacute;n g&acirc;y r&uacute;ng động chốn cung đ&igrave;nh như vụ &aacute;n tai tiếng nhất dưới triều Nguyễn - vụ xử tội &ldquo;h&ograve;a gian&rdquo; của c&ocirc;ng ch&uacute;a Đồng Xu&acirc;n với ch&iacute;nh người anh ruột c&ugrave;ng cha kh&aacute;c mẹ của m&igrave;nh.</p>\r\n\r\n<p>Hầu hết vụ &aacute;n tr&ecirc;n đều được c&aacute;c sử quan ch&eacute;p lại trong c&aacute;c bộ ch&iacute;nh sử như&nbsp;<em>Đại Việt sử k&yacute; to&agrave;n thư, Đại Nam thực lục..</em>. Tuy nhi&ecirc;n, vẫn c&ograve;n những ẩn số trong c&aacute;c vụ &aacute;n do sử xưa ghi ch&eacute;p vắn tắt, hoặc c&oacute; &yacute; kiến cho rằng ph&iacute;a sau ng&ograve;i b&uacute;t sử quan c&oacute; ẩn khuất.</p>\r\n\r\n<p>Kh&ocirc;ng chấp nhận với những điểm c&ograve;n tranh s&aacute;ng, tranh tối trong sử s&aacute;ch xưa, nhiều nh&agrave; văn đ&atilde; cất c&ocirc;ng truy t&igrave;m tư liệu, nghiền ngẫm lịch sử, b&oacute;c t&aacute;ch những vấn đề tồn nghi v&agrave; l&agrave;m r&otilde; những g&oacute;c khuất vốn bị m&ugrave; mờ do c&aacute;c sử gia xưa để lại. Đ&oacute; cũng ch&iacute;nh l&agrave; l&yacute; do để c&aacute;c tiểu thuyết lịch sử c&oacute; t&iacute;nh chất chi&ecirc;u tuyết (rửa sạch oan ức) cho những &aacute;n oan ng&uacute;t trời tr&ecirc;n ra đời.</p>\r\n', 15, 3, 7, '2023-04-04 16:15:21', 2, 0),
(17, 'Giấc mơ 250 tỷ USD với Twitter của Elon Musk', '/uploads/ElonMusk332409484200.jpg', 'Đằng sau thương vụ Elon Musk mua lại Twitter là một âm mưu trị giá 250 tỷ USD mà vị tỷ phú đã ấp ủ từ hơn 20 năm trước: Xây dựng ngân hàng số của riêng mình.', '<p>Vị tỷ ph&uacute; rất &iacute;t khi để lộ về tham vọng n&agrave;y v&agrave; cũng &iacute;t chia sẻ về kế hoạch Twitter 2.0 m&agrave; &ocirc;ng từng đề cập khi mới mua lại c&ocirc;ng ty. Nhưng mới đ&acirc;y, Elon Musk đ&atilde; c&ocirc;ng bố kế hoạch của m&igrave;nh với c&aacute;c nh&acirc;n vi&ecirc;n Twitter.</p>\r\n\r\n<p>Tầm nh&igrave;n của &ocirc;ng l&agrave; đưa Twitter trở th&agrave;nh một c&ocirc;ng ty c&oacute; gi&aacute; trị vốn h&oacute;a gấp 10 lần con số&nbsp;20 tỷ USD&nbsp;hiện tại. Để l&agrave;m được điều đ&oacute;, &ocirc;ng muốn biến Twitter th&agrave;nh trung t&acirc;m t&agrave;i ch&iacute;nh của người d&ugrave;ng.</p>\r\n\r\n<h1><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Hiện thực h&oacute;a ước mơ dang dở v&agrave;o 20 năm trước</strong></h1>\r\n\r\n<p>Theo<em>&nbsp;Wall Street Journal</em>, kế hoạch n&agrave;y tương tự dự định của &ocirc;ng từ những ng&agrave;y đầu lập nghiệp, đồng thời giống với dịch vụ t&agrave;i ch&iacute;nh đầu tay của &ocirc;ng c&oacute; t&ecirc;n l&agrave; X.com, nay đ&atilde; trở th&agrave;nh PayPal.</p>\r\n\r\n<p>Sự th&agrave;nh c&ocirc;ng của X.com từng gi&uacute;p Elon Musk vững mạnh về mặt t&agrave;i ch&iacute;nh, đủ sức tạo ra startup xe điện Tesla v&agrave; c&ocirc;ng ty h&agrave;ng kh&ocirc;ng vũ trụ SpaceX. Nhưng sự kết th&uacute;c của X.com cũng đ&atilde; ch&iacute;nh thức chấm dứt giấc mơ đ&oacute; của &ocirc;ng.</p>\r\n\r\n<p>Nhưng sau khi th&acirc;u t&oacute;m Twitter, Elon Musk lại tiếp tục vẽ ra viễn cảnh m&agrave; người d&ugrave;ng mạng x&atilde; hội n&agrave;y để thanh to&aacute;n cho nhau, vay tiền, nhận l&atilde;i v&agrave; h&agrave;ng loạt t&aacute;c vụ kh&aacute;c chỉ trong một ứng dụng. &ldquo;T&ocirc;i nghĩ n&oacute; c&oacute; thể sẽ trở th&agrave;nh trung t&acirc;m t&agrave;i ch&iacute;nh lớn nhất thế giới&rdquo;, vị tỷ ph&uacute; n&oacute;i trong hội nghị Morgan Stanley hồi th&aacute;ng 3.</p>\r\n\r\n<p>Tại sự kiện, &ocirc;ng đ&atilde; n&oacute;i về tham vọng muốn đa dạng h&oacute;a tiềm năng của Twitter sau khi cải tổ mảng quảng c&aacute;o của c&ocirc;ng ty mạng x&atilde; hội. C&aacute;c nh&agrave; ph&acirc;n t&iacute;ch đều cho rằng, nếu Twitter trở th&agrave;nh một cổng thanh to&aacute;n, doanh thu của c&ocirc;ng ty c&oacute; thể sẽ đạt cột mốc tăng trưởng thần kỳ.</p>\r\n', 27, 3, 6, '2023-04-05 01:55:51', 3, 0),
(18, 'Sau Thanos và Kang, phản diện nào sẽ đặt chân lên màn ảnh?', '/uploads/thanosKang626116714900.jpg', 'Một số ác nhân nổi bật trong Marvel Comics nhiều khả năng sẽ ra mắt khán giả trong tương lai không xa. Đặc biệt khi yếu tố đa vũ trụ được mở ra trong các bộ phim nhà Marvel.', '<p><strong>The Margus</strong>&nbsp;ra mắt lần đầu ti&ecirc;n v&agrave;o năm 1974 trong&nbsp;<em>Strange Tales #178,&nbsp;</em>l&agrave; phi&ecirc;n bản xấu xa trong tương lai của Adam Warlock. Khi Thanos muốn hủy diệt vũ trụ bằng găng tay V&ocirc; cực, hai thực thể Lord Chaos v&agrave; Master Order quyết định sẽ tạo ra một nh&agrave; v&ocirc; địch để chống lại hắn ta. Họ ra lệnh cho tay sai l&agrave; In-Betweener bắt c&oacute;c Adam Warlock, sau đ&oacute; đưa anh ta đến l&atilde;nh giới của m&igrave;nh để bắt anh học c&aacute;ch sống đen tối. Adam Warlock bị chuyển về qu&aacute; khứ 5000 trước tr&ecirc;n h&agrave;nh tinh Sirus X v&agrave; phải sống với triết l&yacute; học được. Ch&iacute;nh điều n&agrave;y đ&atilde; khiến anh ph&aacute;t đi&ecirc;n, trở th&agrave;nh Margus. Ảnh:<em>&nbsp;Screen Rant.</em></p>\r\n\r\n<p><em><img alt=\"\" src=\"http://localhost:8089/userfiles/files/thanosKang.jpg\" style=\"height:600px; width:1200px\" /></em></p>\r\n', 30, 7, 2, '2023-04-05 01:58:15', 4, 0),
(19, 'Chuyện gì sẽ xảy ra với Rap Việt mùa 3?', '/uploads/rapvietmua32119866985000.jpeg', 'Ngoài Wowy, SpaceSpeakers cũng được cho là đã rời Rap Việt. Đó là thách thức của mùa 3, song, sự trở lại của Suboi có thể là điểm đáng chờ đợi.', '<p>Ng&agrave;y 18/3, Rap Việt x&aacute;c nhận trở lại với m&ugrave;a 3. Đơn vị tổ chức bắt đầu casting từ đầu th&aacute;ng 4, tại TP.HCM v&agrave; H&agrave; Nội. Thực tế, m&ugrave;a 2 Rap Việt đ&atilde; giảm sức h&uacute;t so với m&ugrave;a 1, ngo&agrave;i ra rap hiện cũng kh&ocirc;ng l&agrave; &quot;cơn b&atilde;o&quot; như khoảng 2 năm trước. D&ugrave; vậy, chương tr&igrave;nh vẫn được cộng đồng y&ecirc;u rap quan t&acirc;m.</p>\r\n\r\n<p>Việc Wowy th&ocirc;ng b&aacute;o dừng l&agrave;m huấn luyện vi&ecirc;n v&agrave; SpaceSpeakers khả năng cao kh&ocirc;ng tiếp tục tham gia chương tr&igrave;nh, Rap Việt đang đứng trước những th&aacute;ch thức. Về ph&iacute;a th&iacute; sinh, việc ban tổ chức đặt ra quy định kh&ocirc;ng xưng &ldquo;m&agrave;y&rdquo;, &ldquo;tao&rdquo; trong ca từ đang được b&agrave;n luận.</p>\r\n\r\n<h1><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Th&aacute;ch thức cho nh&agrave; sản xuất</strong></h1>\r\n\r\n<p>Ngay sau khi Rap Việt x&aacute;c nhận trở lại với m&ugrave;a 3, th&ocirc;ng tin về d&agrave;n huấn luyện v&agrave; ban gi&aacute;m khảo g&acirc;y ch&uacute; &yacute;, b&agrave;n t&aacute;n. Trong t&igrave;nh thế đ&oacute;, Wowy nhanh ch&oacute;ng l&ecirc;n tiếng thay v&igrave; để kh&aacute;n giả đồn đo&aacute;n. Anh khẳng định kh&ocirc;ng tiếp tục đảm nhận vị tr&iacute; huấn luyện vi&ecirc;n của chương tr&igrave;nh năm nay.</p>\r\n\r\n<p>&quot;Trong 2 m&ugrave;a vừa qua, Wowy rất vui v&igrave; đ&oacute; l&agrave; h&agrave;nh tr&igrave;nh đầy tươi đẹp v&agrave; th&agrave;nh c&ocirc;ng. Đội Wowy gi&agrave;nh được qu&aacute;n qu&acirc;n v&agrave; &aacute; qu&acirc;n cho hai m&ugrave;a ph&aacute;t s&oacute;ng, bằng tất cả sự cố gắng, nỗ lực rất lớn. D&ugrave; kh&ocirc;ng ngồi ghế HLV m&ugrave;a 3, kh&ocirc;ng ch&iacute;nh thức t&igrave;m kiếm những gương mặt t&agrave;i năng cho m&ugrave;a mới, Wowy vẫn lu&ocirc;n ở đ&acirc;y, tiếp tục cống hiến cho rap Việt n&oacute;i chung&quot;, Wowy chia sẻ.</p>\r\n', 29, 5, 0, '2023-04-05 02:23:09', 5, 0),
(20, 'Đến lượt Apple sa thải nhân viên', '/uploads/apple2309367217100.jpg', 'Táo khuyết trở thành cái tên nổi bật trong giới công nghệ khi tránh được làn sóng sa thải nhân viên. Tuy nhiên, có vẻ điều đó vừa kết thúc.', '<p>Theo nguồn tin của&nbsp;<em>Bloomberg</em>&nbsp;v&agrave;&nbsp;<em>Business Insider</em>, Apple&nbsp;đ&atilde; sa thải một &iacute;t vi&ecirc;n từ c&aacute;c nh&oacute;m b&aacute;n lẻ. Hiện tại vẫn chưa r&otilde; c&oacute; bao nhi&ecirc;u người bị ảnh hưởng.</p>\r\n\r\n<p><em>Bloomberg</em>&nbsp;nhấn mạnh con số n&agrave;y &quot;c&oacute; thể rất nhỏ&quot;. Cả 2 nguồn tin đều n&oacute;i rằng nội bộ Apple xem đ&acirc;y l&agrave; một c&aacute;ch để cải thiện hoạt động thay v&igrave; biện ph&aacute;p cắt giảm chi ph&iacute;.</p>\r\n\r\n<p>Động th&aacute;i sa thải một lượng nhỏ nh&acirc;n vi&ecirc;n ở thời điểm n&agrave;y, d&ugrave; l&yacute; do g&igrave;, cũng l&agrave; th&agrave;nh c&ocirc;ng đối với Apple. H&agrave;ng loạt &ocirc;ng lớn c&ocirc;ng nghệ kh&aacute;c đ&atilde; tinh giản nh&acirc;n sự ồ ạt trong v&agrave;i th&aacute;ng qua nhằm cắt giảm chi ph&iacute; hoạt động. Bao gồm Google (12.000 người), Amazon (27.000 người), Meta (21.000 người) v&agrave; Microsoft (10.000 người).</p>\r\n\r\n<p>Theo nhận định của&nbsp;<em>The Verge</em>, việc sa thải nh&acirc;n vi&ecirc;n của Apple dường như ở quy m&ocirc; nhỏ hơn rất nhiều, nhưng cũng đủ khiến tập đo&agrave;n n&agrave;y mất h&igrave;nh mẫu về việc kh&ocirc;ng sa thải nh&acirc;n vi&ecirc;n.</p>\r\n\r\n<p>C&aacute;c vị tr&iacute; bị Apple cắt giảm nằm trong bộ phận xử l&yacute; việc x&acirc;y dựng v&agrave; bảo tr&igrave; cho cửa h&agrave;ng b&aacute;n lẻ. Những nh&acirc;n vi&ecirc;n bị ảnh hưởng c&oacute; thời hạn đến cuối tuần để nộp đơn v&agrave;o c&aacute;c vị tr&iacute; kh&aacute;c tại c&ocirc;ng ty. Apple cũng cung cấp 4 th&aacute;ng trợ cấp th&ocirc;i việc cho những người kh&ocirc;ng thể ở lại, theo&nbsp;<em>Bloomberg</em>.</p>\r\n\r\n<p>Đ&acirc;y l&agrave; lần đầu ti&ecirc;n Apple cắt giảm nh&acirc;n vi&ecirc;n to&agrave;n thời gian kể từ khi l&agrave;n s&oacute;ng sa thải ồ ạt trong lĩnh vực c&ocirc;ng nghệ bắt đầu. Trước đ&oacute; tập đo&agrave;n t&igrave;m c&aacute;ch giảm chi ph&iacute; theo những phương ph&aacute;p kh&aacute;c. Trao đổi với&nbsp;<em>Wall Street Journal</em>, CEO Tim Cook n&oacute;i với rằng sa thải l&agrave; &quot;phương &aacute;n cuối c&ugrave;ng&quot;.</p>\r\n\r\n<p>Nh&agrave; sản xuất Iphone&nbsp;từng giảm bớt chi ph&iacute; hoạt động, hạn chế thu&ecirc; lao động thời vụ, bao gồm c&aacute;c kỹ sư theo hợp đồng, nh&acirc;n vi&ecirc;n tuyển dụng v&agrave; bảo vệ. Trước đại dịch, Apple cũng loại bỏ v&agrave;i trăm nh&acirc;n sự ở bộ phận xe tự h&agrave;nh.</p>\r\n\r\n<p>T&iacute;nh đến th&aacute;ng 9/2022, Apple c&oacute; 164.000 nh&acirc;n vi&ecirc;n. C&ocirc;ng ty kh&ocirc;ng gia tăng lực lượng lao động nhanh ch&oacute;ng như nhiều &ocirc;ng lớn c&ocirc;ng nghệ lớn trong thời kỳ đại dịch, nhờ đ&oacute; cũng &iacute;t chịu &aacute;p lực sa thải hơn.</p>\r\n', 25, 5, 0, '2023-04-05 02:26:18', 1, 0),
(21, 'Trò đùa Cá tháng Tư của Asus thành sự thật', '/uploads/asus6215888377800.jpg', 'Với cách tiếp cận giống Steam Deck hay GPD Win 4, Asus ROG Ally trang bị màn hình 7 inch, chạy Windows 11 và cấu hình mạnh để chơi game.', '<p>Ng&agrave;y 3/4, Asus ra mắt ROG Ally. Đ&acirc;y l&agrave; thiết bị chơi game di động chạy Windows 11 với thiết kế tương tự OneXPlayer 2, Nintendo Switch hay Steam Deck.</p>\r\n\r\n<p>Theo&nbsp;<em>NotebookCheck</em>, ROG Ally đ&atilde; được Asus tiết lộ v&agrave;o ng&agrave;y 1/4, nhưng bị nhiều người cho l&agrave; tr&ograve; đ&ugrave;a C&aacute; th&aacute;ng Tư. Ngay sau đ&oacute;, h&atilde;ng c&ocirc;ng bố đ&acirc;y l&agrave; sản phẩm thực tế, với kế hoạch thương mại h&oacute;a trong thời gian tới.</p>\r\n\r\n<p>Ngoại h&igrave;nh của ROG Ally tương tự c&aacute;c m&aacute;y chơi game cầm tay với m&agrave;n h&igrave;nh lớn, cần analog nằm b&ecirc;n tr&aacute;i v&agrave; phải, n&uacute;t điều hướng 4 chiều v&agrave; 4 n&uacute;t điều khiển, b&ecirc;n cạnh một số n&uacute;t menu v&agrave; c&agrave;i đặt nhanh.</p>\r\n\r\n<p>Về phần cứng, ROG Ally trang bị APU (chip xử l&yacute; kết hợp CPU với GPU) do AMD thiết kế. Theo nguồn tin của&nbsp;<em>ETA Prime</em>, CPU tr&ecirc;n thiết bị sử dụng kiến tr&uacute;c Zen 3, kh&ocirc;ng phải Zen 3+ giống c&aacute;c CPU AMD cao cấp kh&aacute;c/</p>\r\n\r\n<p>Ngược lại, hiệu năng GPU của ROG Ally sẽ mạnh hơn so với Radeon 680M tr&ecirc;n một số chip xử l&yacute; như Ryzen 7 6800U hay Ryzen 9 6980HZ, dựa tr&ecirc;n kiến tr&uacute;c RDNA 3.</p>\r\n\r\n<p>ROG Ally sử dụng m&agrave;n h&igrave;nh 7 inch, tỷ lệ 16:9 với độ ph&acirc;n giải Full HD, độ s&aacute;ng 500 nit v&agrave; tần số qu&eacute;t 120 Hz. Để so s&aacute;nh, Steam Deck trang bị m&agrave;n h&igrave;nh 7 inch, tỷ lệ 16:10, độ ph&acirc;n giải 1.280 x 800 pixel, độ s&aacute;ng 400 nit v&agrave; tần số qu&eacute;t chỉ 60 Hz.</p>\r\n\r\n<p>&nbsp; &nbsp;<img alt=\"\" src=\"http://localhost:8089/userfiles/files/asus.jpg\" style=\"height:400px; width:500px\" /></p>\r\n', 25, 5, 1, '2023-04-05 03:31:25', 2, 0),
(22, 'iPhone X sắp bị Apple bỏ rơi', '/uploads/iphonex7591133694000.jpg', 'Những thiết bị ra mắt từ năm 2017 trở về trước có thể bị dừng cập nhật iOS 17 và iPadOS 17.', '<p>T&agrave;i khoản Twitter&nbsp;<em>@Fame_G_Monsters</em>, chuy&ecirc;n đưa tin về c&aacute;c bản cập nhật phần mềm của Apple, cho biết h&agrave;ng loạt sản phẩm sẽ bị dừng cập nhật iOS 17 v&agrave; iPadOS 17.</p>\r\n\r\n<p>Cụ thể, danh s&aacute;ch thiết bị gồm iPhone 8, iPhone 8 Plus, iPhone X, iPad 9,7 inch 2017 (thế hệ thứ 5), iPad Pro 9,7 inch 2016 v&agrave; iPad Pro 12,9 inch 2015 (thế hệ đầu ti&ecirc;n).</p>\r\n\r\n<p>Nếu tin đồn ch&iacute;nh x&aacute;c, iOS 17 v&agrave; iPadOS 17 sẽ ngừng hỗ trợ hầu hết thiết bị sử dụng chip A11 Bionic hoặc cũ hơn.</p>\r\n\r\n<p>Một số thiết bị ngoại lệ gồm iPad 9,7 inch 2018 (thế hệ 6), iPad 10,2 inch 2019 (thế hệ 7) với chip A10 Fusion. Tiếp đến l&agrave; iPad Pro 10,5 inch v&agrave; 12,9 inch 2017 (thế hệ 2), d&ugrave;ng chip xử l&yacute; A10X Fusion.</p>\r\n\r\n<p>Theo&nbsp;<em>MacRumors</em>, iPhone v&agrave; iPad d&ugrave;ng chip xử l&yacute; từ A5 đến A11 Bionic bị ảnh hưởng bởi lỗ hổng &quot;checkm8&quot;, li&ecirc;n quan đến bootrom của iPhone v&agrave; iPad.</p>\r\n\r\n<p>Bootrom l&agrave; phần bộ nhớ chỉ đọc (boot read-only memory), chứa c&aacute;c đoạn m&atilde; được chạy đầu ti&ecirc;n mỗi khi người d&ugrave;ng khởi động thiết bị iOS.</p>\r\n\r\n<p>Lỗ hổng tạo khả năng c&agrave;i đặt c&aacute;c c&ocirc;ng cụ bẻ kh&oacute;a (jailbreak) vĩnh viễn tr&ecirc;n nhiều phi&ecirc;n bản iOS, cho ph&eacute;p người d&ugrave;ng chỉnh sửa hệ thống. Do thuộc bộ nhớ chỉ đọc, Apple kh&ocirc;ng thể v&aacute; lỗ hổng bootrom trừ khi can thiệp phần cứng.</p>\r\n\r\n<p><img alt=\"\" src=\"http://localhost:8089/userfiles/files/ipad.jpg\" style=\"height:500px; width:500px\" /></p>\r\n', 25, 5, 0, '2023-04-05 03:54:20', 3, 0),
(23, 'Nhìn lại sự nghiệp vĩ đại của tác giả ‘Trăm năm cô đơn’', '/uploads/books1460606223972100.jpg', 'Theo đánh giá của New York Times (NYT), tiểu thuyết gia người Colombia Gabriel García Márquez đã pha trộn giữa hư cấu và thực tế để khắc họa được hiện thực lớn lao của Mỹ Latinh.', '<p>Gabito sinh ra ở Aracataca, Colombia v&agrave;o năm 1927. Mặc d&ugrave; sau đ&oacute; &ocirc;ng thường n&oacute;i nhiều tới năm 1928 - một thời điểm đ&aacute;ng ch&uacute; &yacute; trong lịch sử Colombia. Đ&oacute; l&agrave; năm xảy ra vụ thảm s&aacute;t kh&eacute;t tiếng những c&ocirc;ng nh&acirc;n trồng chuối đ&igrave;nh c&ocirc;ng tr&ecirc;n bờ biển Caribe th&acirc;n y&ecirc;u của &ocirc;ng. Gabito từng n&oacute;i rằng đ&oacute; c&oacute; lẽ l&agrave; k&yacute; ức sớm nhất của &ocirc;ng.</p>\r\n\r\n<p>V&agrave; c&acirc;u chuyện thần thoại về Gabriel Garc&iacute;a M&aacute;rquez đ&atilde; được thế giới biết tới. &Ocirc;ng l&agrave; bậc thầy của chủ nghĩa hiện thực huyền ảo, người đoạt giải Nobel, người đ&atilde; pha trộn giữa thực tại v&agrave; hư cấu để khắc hoạ thực tế lớn lao của đời sống Mỹ Latinh.</p>\r\n\r\n<p>Lượng c&ocirc;ng việc &ocirc;ng l&agrave;m trong cả cuộc đời cũng lớn lao như vậy. Danh mục t&aacute;c phẩm của &ocirc;ng, &iacute;t nhất 24 cuốn bao gồm tiểu thuyết, tiểu thuyết ngắn, tuyển tập truyện v&agrave; t&aacute;c phẩm phi hư cấu, trải d&agrave;i khắp thể loại từ h&igrave;nh sự tội phạm, tiểu thuyết l&atilde;ng mạn, b&igrave;nh luận ch&iacute;nh trị v&agrave; tiểu thuyết lịch sử. T&aacute;c phẩm của Gabriel đ&aacute;p ứng được mọi nhu cầu của độc giả.</p>\r\n', 15, 6, 3, '2023-04-10 09:44:29', 4, 0),
(24, 'Hai mặt của đô thị', '/uploads/books2464664104334400.png', 'Quá trình phát triển, sáng tạo và những góc khuất của đô thị ngày nay đã hiện rõ trong “Metropolis - Lịch sử phát triển đô thị, phát minh lớn nhất của loài người” của Ben Wilson.', '<p>Cuốn&nbsp;<em>Metropolis - Lịch sử ph&aacute;t triển đ&ocirc; thị, ph&aacute;t minh lớn nhất của lo&agrave;i người</em>&nbsp;của nh&agrave; sử học Ben Wilson sẽ đưa độc giả đi qua 26 th&agrave;nh phố lớn - c&aacute;c si&ecirc;u đ&ocirc; thị của thế giới - cả trong qu&aacute; khứ lẫn hiện tại. Qua đ&oacute;, t&aacute;c giả vẽ n&ecirc;n bức tranh sống động về 6.000 năm lịch sử của c&aacute;c si&ecirc;u đ&ocirc; thị (Metropolis) v&agrave; sự ảnh hưởng của n&oacute; đến tiến tr&igrave;nh ph&aacute;t triển, s&aacute;ng tạo của con người.</p>\r\n\r\n<p>Với Wilson, ​​mỗi m&eacute;t vu&ocirc;ng của đ&ocirc; thị ch&iacute;nh l&agrave; sự tương t&aacute;c giữa m&ocirc;i trường, kiến tr&uacute;c với cư d&acirc;n sinh sống b&ecirc;n trong. &Ocirc;ng chỉ ra rằng đ&ocirc; thị l&agrave; một thực thể biến đổi sống động, c&oacute; khả năng vừa định h&igrave;nh lối sống, tư tưởng của c&aacute;c cư d&acirc;n b&ecirc;n trong n&oacute;, lại vừa c&oacute; thể th&aacute;ch thức c&aacute;c gi&aacute; trị v&agrave; hiểu biết của con người.</p>\r\n\r\n<h1><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Hai mặt của một đồng xu</strong></h1>\r\n\r\n<p>Nếu may mắn c&oacute; sức khỏe tốt, cuộc đời của một con người c&oacute; thể k&eacute;o d&agrave;i khoảng trăm năm. Nhưng với c&aacute;c đ&ocirc; thị, tồn tại được h&agrave;ng trăm năm kh&ocirc;ng phải vấn đề qu&aacute; lớn. Đặc biệt, ch&uacute;ng c&oacute; thể &ldquo;sống&rdquo; một thời gian d&agrave;i với &ldquo;hệ sức khỏe&rdquo; đặc biệt, vừa mạnh mẽ lại vừa yếu đuối.</p>\r\n\r\n<p>Trong h&agrave;nh tr&igrave;nh suốt 6.000 năm lịch sử, c&aacute;c khu d&acirc;n cư lớn dần ph&aacute;t triển v&agrave; tạo ra th&agrave;nh phố, tiếp đ&oacute; l&agrave; c&aacute;c đ&ocirc; thị rộng lớn - nơi tập trung kinh tế, ch&iacute;nh trị, văn h&oacute;a c&oacute; ảnh hưởng tr&ecirc;n một v&ugrave;ng rộng lớn. H&agrave;ng loạt đ&ocirc; thị như Babylon, Athens, London, Paris, New York, Los Angeles, Lagos đ&atilde; xuất hiện trong cuốn s&aacute;ch&nbsp;<em>Metropolis - Lịch sử ph&aacute;t triển đ&ocirc; thị, ph&aacute;t minh lớn nhất của lo&agrave;i người</em>&nbsp;của nh&agrave; sử học Ben Wilson.</p>\r\n\r\n<p>T&aacute;c phẩm mang đến cho người đọc g&oacute;c nh&igrave;n đa chiều về đ&ocirc; thị chứ kh&ocirc;ng đơn thuần chỉ l&agrave; n&eacute;t h&agrave;o nho&aacute;ng, xa hoa m&agrave; người ta vẫn thấy trong c&aacute;c bộ phim bom tấn.</p>\r\n\r\n<p>Nếu v&iacute; đ&ocirc; thị l&agrave; một cơ thể sống, sự khỏe mạnh, cường tr&aacute;ng của n&oacute; đến từ nền kinh tế ph&aacute;t triển. Tại đ&acirc;y, những doanh nh&acirc;n, tập đo&agrave;n lớn tề tựu lại để c&ugrave;ng t&igrave;m ra con đường ph&aacute;t triển nhanh v&agrave; mạnh nhất. M&agrave; thể hiện r&otilde; nhất cho sự đi l&ecirc;n của kinh tế ch&iacute;nh l&agrave; c&aacute;c t&ograve;a th&aacute;p chọc trời, c&aacute;c si&ecirc;u kiến tr&uacute;c h&agrave;o nho&aacute;ng.</p>\r\n', 15, 6, 2, '2023-04-10 10:52:06', 5, 0),
(25, 'Nhuận sắc tiếng Việt trong văn học dịch', '/uploads/books3465010044742100.jpg', 'PGS.TS Nguyễn Thị Minh Thái cho rằng khả năng của tiếng Việt là vô tận và đủ phong phú để làm nên những tác phẩm văn học dịch xuất sắc, thuần Việt.', '<p>Nh&acirc;n dịp ra mắt bộ s&aacute;ch&nbsp;<em>Những kẻ phi&ecirc;u lưu</em>&nbsp;của nh&agrave; văn Harold Robbins được dịch bởi dịch giả L&ecirc; Văn Viện, ng&agrave;y 9/4 tại Đường s&aacute;ch TP.HCM, PGS Văn học - TS Nghệ thuật học Nguyễn Thị Minh Th&aacute;i đ&atilde; c&oacute; buổi chia sẻ về chủ đề n&eacute;t đẹp của tiếng Việt trong văn học dịch.</p>\r\n\r\n<h1><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Cuộc c&aacute;ch mạng x&atilde; hội v&agrave; t&igrave;nh dục</strong></h1>\r\n\r\n<p>T&aacute;c phẩm&nbsp;<em>Những kẻ phi&ecirc;u lưu&nbsp;</em>được viết bởi một trong những nh&agrave; văn b&aacute;n chạy nhất mọi thời đại: Harold Robbins (1916 - 1997).</p>\r\n\r\n<p>Theo nh&agrave; xuất bản kỳ cựu Hodder &amp; Stoughton, trong thời của m&igrave;nh, Robbins l&agrave; một &quot;g&atilde; playboy v&agrave; l&agrave; một sư phụ trong xuất bản&quot;. &Ocirc;ng l&agrave; một tiểu thuyết gia nổi tiếng, nhưng những c&acirc;u chuyện về ch&iacute;nh cuộc đời &ocirc;ng thậm ch&iacute; c&ograve;n đậm chất tiểu thuyết hơn cả những cuốn tiểu thuyết của &ocirc;ng. &Ocirc;ng l&agrave; một t&aacute;c giả của cuộc c&aacute;ch mạng x&atilde; hội v&agrave; t&igrave;nh dục.</p>\r\n\r\n<p><em>Những kẻ phi&ecirc;u lưu</em>&nbsp;l&agrave; cuốn tiểu thuyết cuối c&ugrave;ng của nh&agrave; văn, được xuất bản sau khi &ocirc;ng qua đời, ghi dấu l&ograve;ng kh&acirc;m phục v&agrave; nỗi tiếc thương của triệu triệu bạn đọc thế giới d&agrave;nh tặng &ocirc;ng.</p>\r\n\r\n<p>Đ&oacute; l&agrave; c&acirc;u chuyện về ba đời của một d&ograve;ng họ được kể theo những thăng trầm của Dax, nh&acirc;n vật ch&iacute;nh, từ 6 tuổi cho đến ng&agrave;y nhắm mắt l&igrave;a đời. Lớn l&ecirc;n với một tuổi thơ đầy &aacute;m ảnh về c&aacute;i chết bi thảm của mẹ v&agrave; người chị g&aacute;i, cuộc đời Dax trải qua nhiều s&oacute;ng gi&oacute; nhưng cũng kh&ocirc;ng k&eacute;m phần oanh liệt với triền mi&ecirc;n những cay cực, mưu m&ocirc;, bạo lực v&agrave; cả cuộc t&igrave;nh hoang dại, m&ecirc; đắm.</p>\r\n\r\n<p>Với bối cảnh của một quốc gia Nam Mỹ, n&oacute; ghi dấu một giai đoạn lịch sử v&agrave;o trước, trong v&agrave; sau Chiến tranh Thế giới thứ 2 với những &acirc;m mưu ch&iacute;nh trị, ngoại giao, kinh tế, những bữa tiệc xa hoa, những &ocirc;ng tr&ugrave;m dầu lửa, những &ocirc;ng tr&ugrave;m ng&acirc;n h&agrave;ng&hellip;</p>\r\n', 15, 6, 3, '2023-04-10 10:57:52', 1, 0),
(26, 'Tranh luận về ranh giới khi chỉnh sửa tác phẩm kinh điển', '/uploads/books4465178508818800.jpg', 'Các sách của Agatha Christie, Roald Dahl, Ian Fleming đang bị kiểm duyệt lại, cắt bỏ những ngôn từ mang tính xúc phạm. Một số độc giả đặt câu hỏi về ranh giới của công tác này.', '<p>Trong c&aacute;c ấn bản mới của bộ s&aacute;ch thiếu nhi nổi tiếng của Ursula K. Le Guin, c&aacute;c từ như &quot;nh&agrave;m ch&aacute;n&quot;, &quot;đần độn&quot; v&agrave; &quot;ngu ngốc&quot; sẽ bị lược bỏ. Trong tiểu thuyết của Agatha Christie, c&aacute;c thuật ngữ như &ldquo;Phương Đ&ocirc;ng&rdquo;, &ldquo;Gypsy&rdquo; v&agrave; &ldquo;bản địa&rdquo; đ&atilde; bị loại bỏ v&agrave; c&aacute;c phi&ecirc;n bản sửa đổi của loạt s&aacute;ch James Bond của Ian Fleming, người ta cũng cắt hết những cụm từ ph&acirc;n biệt chủng tộc v&agrave; ph&acirc;n biệt giới t&iacute;nh. Trong c&aacute;c t&aacute;c phẩm kinh điển của Roald Dahl, bi&ecirc;n tập vi&ecirc;n cũng x&oacute;a hết c&aacute;c t&iacute;nh từ như &ldquo;b&eacute;o&rdquo;, &ldquo;xấu x&iacute;&rdquo;, c&aacute;c từ chỉ giới t&iacute;nh v&agrave; m&agrave;u da của nh&acirc;n vật.</p>\r\n\r\n<p>Theo&nbsp;<em>New York Times</em>, khi tin tức về việc c&aacute;c t&aacute;c phẩm kinh điển bị chỉnh sửa lan ra, c&acirc;u hỏi về c&aacute;ch c&aacute;c t&aacute;c phẩm n&agrave;y được cập nhật cho ph&ugrave; hợp với độ nhạy cảm của c&ocirc;ng ch&uacute;ng ng&agrave;y nay đ&atilde; g&acirc;y chia rẽ văn đ&agrave;n.</p>\r\n\r\n<h1>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <strong>&nbsp;Văn đ&agrave;n dậy s&oacute;ng</strong></h1>\r\n\r\n<p>Những nỗ lực kiểm duyệt hiện nay đang thu h&uacute;t c&aacute;i nh&igrave;n gay gắt của c&ocirc;ng ch&uacute;ng.</p>\r\n\r\n<p>C&aacute;c nh&agrave; xuất bản v&agrave; c&aacute;c đơn vị l&agrave;m s&aacute;ch trở th&agrave;nh t&acirc;m điểm của một cuộc tranh luận s&ocirc;i nổi trong khi họ đang vật lộn xem l&agrave;m thế n&agrave;o để vừa bảo tồn &yacute; định ban đầu của t&aacute;c giả, vừa đảm bảo rằng t&aacute;c phẩm của họ tiếp tục g&acirc;y được tiếng vang v&agrave; b&aacute;n được.</p>\r\n', 16, 0, 0, '2023-04-10 11:00:41', 2, 0),
(27, 'Giới thiệu truyện hư cấu của Jorge Luis Borges đến Việt Nam', '/uploads/books5465625763669300.jpg', '\"Truyện hư cấu\" kết hợp nhuần nhuyễn triết học với giả tưởng, tập hợp các câu chuyện được liên kết với nhau một cách kỳ lạ, lu mờ giữa ranh giới tiểu luận và truyện ngắn.', '<p>Jorge Luis Borges (1899 - 1986) l&agrave; nh&agrave; văn, nh&agrave; thơ nổi tiếng người Argentina. Sinh thời, &ocirc;ng kh&ocirc;ng phải l&agrave; một t&aacute;c giả được coi trọng. &Ocirc;ng sống trong ngh&egrave;o kh&oacute;, phải l&agrave;m thủ thư để kiếm sống.</p>\r\n\r\n<p>M&atilde;i đến năm 1941,&nbsp;<em>Truyện hư cấu</em>&nbsp;đoạt giải thưởng lớn danh dự của Hội nh&agrave; văn Argentina, Borges mới được văn đ&agrave;n để &yacute;. Năm 1961, khi &ocirc;ng v&agrave; Samuel Beckett nhận giải Formentor Prize, c&aacute;c t&aacute;c phẩm của Borges mới dần được nh&igrave;n nhận như những t&aacute;c phẩm kinh điển của văn chương thế kỷ XX. Từ đ&oacute;, &ocirc;ng cũng thường xuy&ecirc;n được nhắc đến như cha đẻ của chủ nghĩa hiện thực huyền ảo.</p>\r\n\r\n<p>Với&nbsp;<em>Truyện hư cấu</em>, Borges đưa độc giả l&ecirc;n h&agrave;nh tr&igrave;nh v&agrave;o c&otilde;i huyền ảo, đầy hấp dẫn của si&ecirc;u hư cấu (metafiction). T&aacute;c phẩm kết hợp nhuần nhuyễn triết học với giả tưởng, tập hợp c&aacute;c c&acirc;u chuyện được li&ecirc;n kết với nhau một c&aacute;ch kỳ lạ, lu mờ giữa ranh giới tiểu luận v&agrave; truyện ngắn. Borges viết những g&igrave; &ocirc;ng muốn với một sự tao nh&atilde; kh&ocirc;ng khoan nhượng.</p>\r\n\r\n<p>Theo dịch giả Nguyễn An L&yacute;, như nhiều tập s&aacute;ch kh&aacute;c của Jorge Luis Borges, bản th&acirc;n lịch sử cuốn&nbsp;<em>Truyện hư cấu</em>&nbsp;kh&ocirc;ng đơn giản. Năm 1941, &ocirc;ng cho ph&aacute;t h&agrave;nh tập s&aacute;ch&nbsp;<em>El jard&iacute;n de senderos que se bifurcan</em>&nbsp;(tạm dịch: Khu vườn những lối đi rẽ đ&ocirc;i), tập hợp c&aacute;c truyện ngắn đăng b&aacute;o trong khoảng 1939-1941 c&ugrave;ng một truyện chưa từng được xuất bản l&agrave;&nbsp;<em>El jard&iacute;n de senderos que se bifurcan</em>. Tập s&aacute;ch n&agrave;y ch&iacute;nh l&agrave; nửa đầu của&nbsp;<em>Truyện hư cấu</em>.</p>\r\n\r\n<p>Sang giai đoạn 1942-1944, c&aacute;c truyện ngắn mới được tập hợp lại, th&agrave;nh tập&nbsp;<em>Artificos</em>&nbsp;(T&aacute;c tạo), in c&ugrave;ng tập&nbsp;<em>El jard&iacute;n de senderos que se bifurcan</em>&nbsp;năm xưa v&agrave; một ghi ch&eacute;p trong tập tiểu luận&nbsp;<em>Historia de la eternidad</em>&nbsp;(Lịch sử Vĩnh cửu, 1936) th&agrave;nh một tập s&aacute;ch chung c&oacute; t&ecirc;n&nbsp;<em>Ficciones</em>&nbsp;(Truyện hư cấu) v&agrave;o năm 1944.</p>\r\n\r\n<p>Năm 1956, trong lần t&aacute;i bản mới,&nbsp;<em>Truyện hư cấu</em>&nbsp;được bổ sung th&ecirc;m 3 truyện ngắn.</p>\r\n\r\n<p>Theo dịch giả Nguyễn An L&yacute;, dịch Borges l&agrave; một c&ocirc;ng việc vừa hứng th&uacute; vừa nhọc nhằn, vừa phải l&agrave; một phần kh&ocirc;ng thể thiếu trong nghi&ecirc;n cứu - hoặc t&igrave;m hiểu về nghi&ecirc;n cứu - Borges, vừa l&agrave; một sự đi tr&ecirc;n d&acirc;y mong manh giữa kh&ocirc;ng diễn giải đủ v&agrave; diễn giải qu&aacute; đ&agrave;.</p>\r\n\r\n<p>Theo đ&oacute;, dịch giả An L&yacute; cố gắng hướng bản dịch th&agrave;nh một bản dịch phổ th&ocirc;ng tốt. B&agrave; thực hiện chuyển ngữ dựa tr&ecirc;n cơ sở tham khảo tổng hợp c&aacute;c bản dịch tiếng Anh: do Andrew Hurley dịch trong&nbsp;<em>Collected Fictions</em>&nbsp;(To&agrave;n tập truyện ngắn, Penguin, 1998); nhiều người dịch trong&nbsp;<em>Ficciones</em>&nbsp;(Anthony Kerrigan chủ bi&ecirc;n, Grove Press, 1962); c&aacute;c truyện li&ecirc;n quan do nhiều người dịch trong&nbsp;<em>Labyrinths</em>&nbsp;(M&ecirc; cung, Donald A. Yates v&agrave; James E. Irby chủ bi&ecirc;n, New Directions, 1964); v&agrave; c&aacute;c truyện li&ecirc;n quan do Norman Thomas di Giovanni dịch, chủ yếu tập hợp trong&nbsp;<em>The Aleph and Other Stories 1933-1969</em>&nbsp;(&ldquo;Aleph&rdquo; v&agrave; c&aacute;c truyện ngắn kh&aacute;c, 1933-1969, E. P. Dutton, 1970); c&oacute; đối chiếu với nguy&ecirc;n bản tiếng T&acirc;y Ban Nha, c&ugrave;ng tham khảo nhiều nguồn tư liệu, ch&uacute; giải, nghi&ecirc;n cứu.</p>\r\n\r\n<p>Dịch giả cho biết phần tr&igrave;nh b&agrave;y trong bản dịch&nbsp;<em>Truyện hư cấu</em>&nbsp;n&agrave;y chủ yếu dựa theo văn bản trong cuốn&nbsp;<em>Borges: Obras completas</em>&nbsp;[tập 1], 1923-1972 (Borges: To&agrave;n tập, 1923-1972, Emec&eacute; Editores, 1974), nhận thức r&otilde; rằng cuốn n&agrave;y c&oacute; một số lỗi m&agrave; c&aacute;c nh&agrave; nghi&ecirc;n cứu đ&atilde; chỉ ra, do vậy, b&agrave; cẩn trọng khi sử dụng.</p>\r\n\r\n<p>C&oacute; thể n&oacute;i, t&aacute;c phẩm của Borges kh&ocirc;ng chỉ th&aacute;ch thức dịch giả m&agrave; th&aacute;ch thức cả người đọc. Nhưng đ&oacute; cũng ch&iacute;nh l&agrave; c&aacute;ch &ocirc;ng đ&atilde; th&uacute;c đẩy tiến tr&igrave;nh ph&aacute;t triển của văn chương. Cho đến tận ng&agrave;y nay, t&aacute;c phẩm của &ocirc;ng vẫn truyền cảm hứng cho nhiều thế hệ viết văn, trong đ&oacute; c&oacute; c&aacute;c t&ecirc;n tuổi như Gabriel Garcia Marquez, Umberto Eco hay Salman Rushdie...</p>\r\n', 15, 10, 1, '2023-04-10 11:08:08', 3, 0),
(28, 'Gỡ biển \'Cấm dừng xe quá 5 phút\' tại trạm thu phí', '/uploads/xahoi1234208015458100.jpeg', 'Cục Đường bộ Việt Nam vừa yêu cầu tháo dỡ các biển báo không còn phù hợp tại khu vực các trạm thu phí.', '<p><img src=\"https://znews-photo.zingcdn.me/w960/Uploaded/divbzivo/2023_04_02/img_bgt_2021_bien_bao_1681114476_width1280height720.jpeg\" /></p>\r\n\r\n<p>Cục Đường bộ Việt Nam vừa c&oacute; văn bản y&ecirc;u cầu c&aacute;c doanh nghiệp dự &aacute;n, nh&agrave; đầu tư BOT, Tổng c&ocirc;ng ty Đầu tư v&agrave; ph&aacute;t triển đường cao tốc Việt Nam (VEC) r&agrave; so&aacute;t, chủ động th&aacute;o dỡ c&aacute;c biển b&aacute;o kh&ocirc;ng c&ograve;n ph&ugrave; hợp với h&igrave;nh thức thu ph&iacute; kh&ocirc;ng dừng tại khu vực c&aacute;c trạm thu ph&iacute; do đơn vị quản l&yacute;.</p>\r\n\r\n<p>Theo cơ quan n&agrave;y, tại một số trạm thu ph&iacute; c&ograve;n c&aacute;c biển b&aacute;o chưa ph&ugrave; hợp với h&igrave;nh thức thu ph&iacute; tự động kh&ocirc;ng dừng như biển &quot;Cấm dừng xe qu&aacute; 5 ph&uacute;t&quot;; &quot;Cửa nhận v&eacute;&quot;...</p>\r\n\r\n<p>&quot;C&aacute;c nh&agrave; đầu tư, doanh nghiệp dự &aacute;n BOT, VEC khẩn trương r&agrave; so&aacute;t, chủ động th&aacute;o dỡ c&aacute;c biển b&aacute;o kh&ocirc;ng c&ograve;n ph&ugrave; hợp với h&igrave;nh thức thu ph&iacute; kh&ocirc;ng dừng tại khu vực c&aacute;c trạm thu ph&iacute; do đơn vị m&igrave;nh quản l&yacute;. Biển b&aacute;o hiệu thu hồi phải được quản l&yacute;, bảo quản theo quy định&quot;, Cục Đường bộ y&ecirc;u cầu.</p>\r\n\r\n<p>Trước đ&oacute;, Tổng cục Đường bộ Việt Nam (nay l&agrave; Cục Đường bộ Việt Nam) đ&atilde; y&ecirc;u cầu c&aacute;c đơn vị quản l&yacute; đường bộ v&agrave; nh&agrave; đầu tư BOT r&agrave; so&aacute;t hiện trạng biển b&aacute;o tại c&aacute;c cửa trạm thu ph&iacute; để tổ chức giao th&ocirc;ng theo hướng lắp đặt biển số P.131 &quot;Cấm đỗ xe&quot; ph&iacute;a trước trạm thu ph&iacute; khoảng 100-200m, t&ugrave;y theo điều kiện cụ thể để x&aacute;c định khoảng c&aacute;ch đặt cho ph&ugrave; hợp.</p>\r\n\r\n<p>Thời điểm đ&oacute;, đơn vị n&agrave;y cũng y&ecirc;u cầu lắp đặt biển &quot;Cấm dừng xe qu&aacute; 5 ph&uacute;t&quot; tại trạm thu ph&iacute;.</p>\r\n\r\n<p>Y&ecirc;u cầu n&agrave;y đ&atilde; g&acirc;y bất b&igrave;nh trong giới t&agrave;i xế tuy nhi&ecirc;n ph&iacute;a Tổng cục khẳng định họ ho&agrave;n to&agrave;n c&oacute; đủ thẩm quyền đưa ra quyết định dựng biển n&agrave;y. Đ&acirc;y được cho l&agrave; giải ph&aacute;p nhằm đối ph&oacute; với t&igrave;nh trạng &ugrave;n tắc k&eacute;o d&agrave;i dẫn đến mất an ninh trật tự đang diễn ra ở c&aacute;c trạm BOT thời điểm đ&oacute;.</p>\r\n', 44, 6, 24, '2023-04-17 03:14:50', 4, 0),
(29, 'Những thực phẩm cần thiết cho phụ nữ mang thai vào mùa hè', '/uploads/suckhoe1235096230073900.jpg', 'Theo India, thịt, các loại hạt, sản phẩm từ sữa, ngũ cốc nguyên hạt là những thực phẩm giúp phụ nữ mang thai tăng cường hệ miễn dịch trong thời điểm mùa hè sắp đến.', '<p><img alt=\"\" src=\"http://localhost:8089/userfiles/files/suckhoe1.jpg\" style=\"height:300px; width:450px\" /></p>\r\n\r\n<p>Đối với phụ nữ, mang thai được v&iacute; như trải qua một chuyến t&agrave;u lượn si&ecirc;u tốc, v&igrave; l&uacute;c n&agrave;y, cơ thể sẽ c&oacute; nhiều thay đổi về chỉ số cảm x&uacute;c rất nhanh ch&oacute;ng. D&ugrave; vậy, điều quan trọng l&agrave; phụ nữ phải duy tr&igrave; một lối sống l&agrave;nh mạnh v&agrave; chế độ ăn uống bổ dưỡng.</p>\r\n\r\n<p>Khi m&ugrave;a h&egrave; cận kề c&ugrave;ng biến đổi kh&iacute; hậu v&agrave; dịch c&uacute;m đang diễn ra phức tạp,&nbsp;<em>India</em>&nbsp;khuyến nghị thai phụ n&ecirc;n bổ sung nhiều vitamin, kho&aacute;ng chất, chất xơ, tr&aacute;i c&acirc;y v&agrave; rau quả v&agrave;o chế độ ăn uống h&agrave;ng ng&agrave;y. N&oacute; sẽ cung cấp năng lượng, giữ cho cơ thể đủ nước v&agrave; cảm gi&aacute;c no suốt m&ugrave;a h&egrave;.</p>\r\n\r\n<p>Về cơ bản, c&aacute;c nguy&ecirc;n tắc của chế độ uống l&agrave;nh mạnh vẫn kh&ocirc;ng thay đổi khi mang thai, đ&oacute; l&agrave; ăn nhiều tr&aacute;i c&acirc;y, rau, ngũ cốc nguy&ecirc;n hạt, protein nạc v&agrave; chất b&eacute;o l&agrave;nh mạnh.</p>\r\n', 51, 6, 1, '2023-04-17 03:29:38', 5, 0),
(30, 'Messi sẽ rời PSG theo dạng chuyển nhượng tự do?', '/uploads/sport5760539772045300.jpg', 'Khả năng Lionel Messi tái hợp Barcelona ngày càng cao bởi đội chủ sân Parc des Princes dường như không còn tha thiết muốn giữ chân siêu sao người Argentina bằng mọi giá.', '<p>Nguồn tin của&nbsp;<em>Mirror&nbsp;</em>tiết lộ PSG kh&ocirc;ng vội tiến h&agrave;nh đ&agrave;m ph&aacute;n gia hạn v&agrave; k&yacute; hợp đồng mới với Messi. Thay v&agrave;o đ&oacute;, họ sẵn s&agrave;ng để nh&agrave; v&ocirc; địch World Cup 2022 ra đi theo dạng tự do trong kỳ chuyển nhượng m&ugrave;a h&egrave; năm nay.</p>\r\n\r\n<p><em>Goal</em>&nbsp;cũng cho biết th&ecirc;m rằng Chủ tịch Nasser Al-Khelaifi cam kết ph&aacute;t triển PSG trở th&agrave;nh một CLB bền vững, ch&uacute; trọng v&agrave;o c&aacute;c t&agrave;i năng người Ph&aacute;p nhằm mục đ&iacute;ch &iacute;t phụ thuộc v&agrave;o c&aacute;c ng&ocirc;i sao t&ecirc;n tuổi. Động th&aacute;i n&agrave;y mở ra cơ cho Barcelona chi&ecirc;u mộ Messi. Nguồn tin của Goal rất đ&aacute;ng ch&uacute; &yacute; v&igrave; đ&acirc;y l&agrave; tờ b&aacute;o th&acirc;n thiết với Messi, cũng l&agrave; nơi anh thực hiện cuộc phỏng vấn chấn động l&agrave;ng t&uacute;c cầu sau khi chia tay&nbsp;<a href=\"https://zingnews.vn/tieu-diem/barcelona-fc.html\">Barca</a>.</p>\r\n\r\n<p>Đội chủ s&acirc;n Camp Nou sẵn s&agrave;ng chuyển nhượng tự do Messi v&agrave; trao cho &quot;El Pulga&quot; hợp đồng hai năm. Song, Leo sẽ chỉ nhận được mức lương khoảng 25 triệu euro mỗi m&ugrave;a trước thuế. Mức đ&atilde;i ngộ n&agrave;y chỉ bằng 25% thu nhập cũ của anh trước khi rời Camp Nou để gia nhập PSG v&agrave;o m&ugrave;a h&egrave; năm 2021.</p>\r\n\r\n<p>Đ&aacute;ng ch&uacute; &yacute;, tồn tại điều khoản cho ph&eacute;p nh&agrave; v&ocirc; địch World Cup 2022 đơn phương chấm dứt hợp đồng sau m&ugrave;a đầu ti&ecirc;n. Leo muốn trở lại &quot;Blaugrana&quot; trước khi kết th&uacute;c sự nghiệp lẫy lừng ở ch&acirc;u &Acirc;u. Sau đ&oacute;, anh sẽ c&acirc;n nhắc c&aacute;c đề nghị từ MLS hoặc Saudi Arabia.</p>\r\n\r\n<p>Truyền th&ocirc;ng Ph&aacute;p cho biết PSG sớm đưa v&agrave;o tầm ngắm hai cầu thủ tấn c&ocirc;ng trong trường hợp chia tay Messi. Victor Osimhen (<a href=\"https://zingnews.vn/tieu-diem/napoli-fc.html\">Napoli</a>) v&agrave; Randal Kolo Muani (Frankfurt) l&agrave; hai mục ti&ecirc;u của CLB chủ s&acirc;n Parc des Princes, theo&nbsp;<em>Le Parisien.</em></p>\r\n\r\n<p>L&atilde;nh đạo PSG được cho đ&atilde; sẵn s&agrave;ng chia tay cả Messi v&agrave; Neymar để x&acirc;y dựng tập thể xung quanh&nbsp;<a href=\"https://zingnews.vn/tieu-diem/kylian-mbappe.html\">Kylian Mbappe</a>. Ở kỳ chuyển nhượng h&egrave; tới, CLB thủ đ&ocirc; Paris sẽ tập trung chi&ecirc;u mộ nhiều cầu thủ trẻ trung thay v&igrave; đ&oacute;n những ng&ocirc;i sao đắt gi&aacute; về thi đấu như những năm gần đ&acirc;y.</p>\r\n\r\n<p>&quot;T&ocirc;i kh&ocirc;ng r&otilde; điều g&igrave; sẽ xảy ra tiếp theo... Tương lai của t&ocirc;i sẽ ra sao...&quot;, Messi chia sẻ trong một chiến dịch quảng c&aacute;o cho Louis Vuitton h&ocirc;m 15/4, &quot;T&ocirc;i th&iacute;ch tưởng tượng, nghĩ về những điều c&oacute; thể xảy ra. Nhưng t&ocirc;i thực sự kh&ocirc;ng biết tương lai của m&igrave;nh sẽ ra sao. Bất cứ điều g&igrave; Ch&uacute;a muốn n&oacute; xảy ra, n&oacute; sẽ đến&quot;.</p>\r\n', 9, 6, 0, '2023-04-23 05:26:40', 1, 0);
INSERT INTO `news` (`id`, `title`, `thumbnail`, `shortdescription`, `content`, `cate_id`, `flag`, `view`, `insert_time`, `user_id`, `status`) VALUES
(31, 'Arteta: Không thể chờ đến lúc cùng Arsenal đánh bại Man City', '/uploads/sport6760639925781100.jpg', 'HLV Mikel Arteta sẵn sàng để giúp Arsenal vượt qua Man City trong trận đấu được coi là chung kết của Premier League mùa giải năm nay.', '<p>&quot;T&ocirc;i kh&ocirc;ng thể chờ đợi th&ecirc;m, đ&acirc;y l&agrave; kiểu trận đấu m&agrave; bạn muốn g&oacute;p mặt&quot;, Arteta n&oacute;i với&nbsp;<em>BBC Sport,</em>&nbsp;&quot;Khi mọi thứ đang bị đe dọa, bạn phải đến đ&oacute; để gi&agrave;nh chiến thắng. Ch&uacute;ng t&ocirc;i sẽ chuẩn bị kỹ c&agrave;ng cho chiến thắng, đ&oacute; l&agrave; điều chắc chắn. C&aacute;c cầu thủ kh&ocirc;ng hề mất đi tự tin. Họ lu&ocirc;n chấp nhận rủi ro v&agrave; chủ động trong việc triển khai b&oacute;ng&quot;.</p>\r\n\r\n<p>&quot;Trận đấu mang tới vẻ đẹp của m&ocirc;n thể thao vua. Bạn lu&ocirc;n muốn chiến đấu cho những trận cầu quan trọng như thế n&agrave;y. Ch&uacute;ng t&ocirc;i sẽ c&oacute; một chuyến đi tuyệt vời tới Manchester v&agrave; sẽ chuẩn bị thật tốt. Ch&uacute;ng t&ocirc;i kh&ocirc;ng đ&aacute;nh mất đi sự tự tin&quot;, HLV người T&acirc;y Ban Nha khẳng định.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, cựu tiền đạo Premier League, Gabriel Agbonlahor, cho rằng Arsenal kh&oacute; thắng Man City: &quot;Rob Holding l&agrave; một hậu vệ hạng Championship. Cậu ấy kh&ocirc;ng đủ giỏi để trở th&agrave;nh lựa chọn thứ ba ở một đội b&oacute;ng cạnh tranh danh hiệu. Kể từ khi William Saliba d&iacute;nh chấn thương, họ để thủng lưới 9 b&agrave;n trong 5 trận với 3 trận h&ograve;a li&ecirc;n tiếp, đ&oacute; kh&ocirc;ng phải phong độ của một nh&agrave; v&ocirc; địch&quot;.</p>\r\n\r\n<p>&quot;T&ocirc;i thật sự nghĩ rằng Man City sẽ vượt qua Arsenal 3 hoặc 4 b&agrave;n. Riyad Mahrez hoặc Bernardo Silva sẽ g&acirc;y kh&oacute; dễ cho Zinchenko. Haaland th&igrave; n&ocirc;n n&oacute;ng đối đầu Holding v&agrave; Gabriel&quot;, Agbonlahor kết luận.</p>\r\n\r\n<p>H&ocirc;m 22/4, Arsenal kh&ocirc;ng thể thắng dễ Southampton. Họ bị đối thủ dẫn trước 2-0 chỉ sau 14 ph&uacute;t, với c&aacute;c pha lập c&ocirc;ng của Carlos Alcaraz v&agrave; Theo Walcott. Khi kim đồng hồ chỉ đến ph&uacute;t 87, Ph&aacute;o thủ vẫn bị đội kh&aacute;ch dẫn trước với tỷ số 3-1. Rất may, Martin Odegaard v&agrave; Bukayo Saka kịp thời ghi b&agrave;n để mang về một trận h&ograve;a 3-3.</p>\r\n\r\n<p>Với 1 điểm c&oacute; được trước Southampton, Arsenal tạm thời dẫn trước Man City 5 điểm tr&ecirc;n BXH. Tuy nhi&ecirc;n, thầy tr&ograve; Mikel Arteta đ&atilde; chơi nhiều hơn đối thủ hai trận. V&agrave;o ng&agrave;y 27/4 sắp tới đ&acirc;y, Arsenal v&agrave; Man City sẽ đụng độ nhau.</p>\r\n', 9, 6, 0, '2023-04-23 05:28:20', 1, 0),
(32, 'HLV Park Hang-seo cảm động khi gặp lại Hùng Dũng', '/uploads/sport7760719834281500.jpg', 'Cựu thuyền trưởng tuyển Việt Nam không giấu được niềm vui khi gặp lại cậu học trò cũ, tiền vệ Đỗ Hùng Dũng.', '<p>Ng&agrave;y 22/4, HLV Park chia sẻ những khoảnh khắc gặp lại H&ugrave;ng Dũng tr&ecirc;n trang c&aacute; nh&acirc;n k&egrave;m d&ograve;ng m&ocirc; tả: &quot;Xem ai bay từ Việt Nam sang đ&acirc;y để thăm t&ocirc;i n&agrave;y. T&ocirc;i cảm động lắm&quot;.</p>\r\n\r\n<p>Thời điểm n&agrave;y, V.League 2023 tạm dừng để phục vụ U22 Việt Nam dự SEA Games 32. H&ugrave;ng Dũng tận dụng qu&atilde;ng nghỉ n&agrave;y để sang H&agrave;n Quốc thăm người thầy cũ v&agrave; đồng đội ở đội tuyển quốc gia, tiền đạo Nguyễn Văn To&agrave;n.</p>\r\n\r\n<p>H&ugrave;ng Dũng cũng dự kh&aacute;n một trận đấu của CLB Seoul E-Land c&ugrave;ng Văn To&agrave;n v&agrave; HLV Park. Một điều đ&aacute;ng tiếc l&agrave; anh chưa thể xem đ&agrave;n em chơi b&oacute;ng tại K.League 2. Văn To&agrave;n d&iacute;nh chấn thương lật cổ ch&acirc;n hồi cuối th&aacute;ng 3 v&agrave; chưa thể trở lại.</p>\r\n', 8, 6, 0, '2023-04-23 05:29:40', 2, 0),
(33, 'Sân bay Phan Thiết sau gần 1 thập kỷ khởi công', '/uploads/buss760843849402200.jpg', 'Được khởi công vào ngày 18/1/2015, đến nay sân bay Phan Thiết vẫn còn nhiều vướng mắc trong quá trình triển khai xây dựng.', '<p>S&acirc;n bay Phan Thiết được x&acirc;y dựng tại x&atilde; Thiện Nghiệp (TP Phan Thiết, tỉnh B&igrave;nh Thuận) c&aacute;ch TP Phan Thiết khoảng 15 km. Dự &aacute;n được khởi c&ocirc;ng v&agrave;o th&aacute;ng 1/2015 theo h&igrave;nh thức BT kết hợp BOT, thời điểm đ&oacute; c&ocirc;ng tr&igrave;nh được dự kiến ho&agrave;n th&agrave;nh trong năm 2018. Tuy nhi&ecirc;n, do gặp kh&oacute; khăn khi triển khai, đến năm 2021 dự &aacute;n mới được t&aacute;i khởi động.</p>\r\n', 19, 6, 0, '2023-04-23 05:31:44', 2, 0),
(34, 'Dinh thự từng là phim trường James Bond được rao giá 93 triệu USD', '/uploads/buss1760954382730600.jpeg', 'Bất động sản rộng hơn 2.650 m2 này từng là nơi ở của vương tộc Bonaparte, chủ ngân hàng JP Morgan và nhà sản xuất phim Harry Saltzman.', '<p>Tổng diện t&iacute;ch khu&ocirc;n vi&ecirc;n dinh thự khoảng 2.650 m2. Đ&acirc;y từng l&agrave; nơi ở của nhiều người nổi tiếng v&agrave; quyền lực, bao gồm vi&ecirc;n cảnh s&aacute;t trưởng cấp cao của Buckinghamshire (Anh), những th&agrave;nh vi&ecirc;n trong ho&agrave;ng gia Bonaparte, chủ ng&acirc;n h&agrave;ng JP Morgan v&agrave; nh&agrave; sản xuất phim Harry Saltzman.</p>\r\n', 19, 6, 0, '2023-04-23 05:33:34', 3, 0),
(35, 'Cổ đông Tesla lại phàn nàn Elon Musk vì xao nhãng việc điều hành', '/uploads/buss2761030131391400.png', 'Một nhóm cổ đông Tesla bày tỏ mong muốn HĐQT công ty buộc CEO Elon Musk tập trung nhiều hơn cho hãng xe điện để giải quyết các vấn đề gần đây.', '<p>Theo&nbsp;<em>CNN</em>, trong bức thư gửi tới HĐQT Tesla, nh&oacute;m cổ đ&ocirc;ng n&agrave;y cho rằng Elon Musk đang kh&ocirc;ng d&agrave;nh đủ thời gian v&agrave; t&acirc;m tr&iacute; cho c&aacute;c vấn đề của c&ocirc;ng ty xe điện.</p>\r\n\r\n<p>Nhất l&agrave; trong bối cảnh cạnh tranh gia tăng từ c&aacute;c h&atilde;ng xe điện kh&aacute;c khiến m&ocirc;i trường l&agrave;m việc tại Tesla đang dần trở n&ecirc;n độc hại hơn.</p>\r\n\r\n<p>Được biết, đ&acirc;y kh&ocirc;ng phải l&agrave; lần đầu ti&ecirc;n Musk vướng v&agrave;o những chỉ tr&iacute;ch v&agrave; ph&agrave;n n&agrave;n từ cổ đ&ocirc;ng về việc xao nh&atilde;ng điều h&agrave;nh. Ngo&agrave;i vị tr&iacute; CEO h&atilde;ng xe điện, &ocirc;ng hiện c&ograve;n l&atilde;nh đạo nhiều c&ocirc;ng ty kh&aacute;c như Twitter, SpaceX, The Boring Company v&agrave; cả Neuralink.</p>\r\n', 18, 6, 0, '2023-04-23 05:34:50', 3, 0),
(36, 'Phù thủy truyền thông của Apple dưới thời Steve Jobs qua đời', '/uploads/tech761136466876400.jpg', 'Katie Cotton, Giám đốc truyền thông lâu năm của Apple, người tạo ra phong cách bí mật thông tin đặc trưng của Táo khuyết vừa qua đời tuần qua.', '<p>&ldquo;Ch&uacute;ng t&ocirc;i v&ocirc; c&ugrave;ng đau buồn trước sự ra đi của Katie&rdquo;,&nbsp;<a href=\"https://zingnews.vn/tieu-diem/cong-ty-apple.html\">Apple</a>&nbsp;cho biết trong một tuy&ecirc;n bố ng&agrave;y 10/4.</p>\r\n\r\n<p>&ldquo;B&agrave; ấy l&agrave; một người phi thường v&agrave; đ&atilde; c&oacute; v&ocirc; số đ&oacute;ng g&oacute;p trong suốt sự nghiệp k&eacute;o d&agrave;i 2 thập kỷ của m&igrave;nh tại Apple. Ch&uacute;ng t&ocirc;i chia buồn với những người th&acirc;n y&ecirc;u của c&ocirc; ấy v&agrave; tất cả những người đ&atilde; c&oacute; cơ hội l&agrave;m việc với c&ocirc; ấy&quot;, tuy&ecirc;n bố của Apple ghi r&otilde;.</p>\r\n\r\n<p>Cotton được bổ nhiệm l&agrave;m Ph&oacute; chủ tịch truyền th&ocirc;ng của Apple v&agrave;o năm 1996 v&agrave; giữ vai tr&ograve; đ&oacute; cho đến khi nghỉ hưu v&agrave;o năm 2014. B&agrave; l&agrave; người đ&atilde; x&acirc;y dựng chiến lược truyền th&ocirc;ng của c&ocirc;ng ty v&agrave; gi&uacute;p tổ chức c&aacute;c sự kiện ra mắt g&acirc;y nhiều tiếng vang. Cotton nổi tiếng l&agrave; người đ&atilde; hỗ trợ Jobs trong thời kỳ sức khỏe của vị CEO suy yếu.</p>\r\n', 25, 6, 0, '2023-04-23 05:36:36', 4, 0),
(37, 'Laptop LG gram 2023 đồng hành cùng thế hệ \'Làm chất - Cân tất\'', '/uploads/tech1761207656054100.jpg', 'Với những cải tiến ấn tượng, LG gram 2023 nâng tầm giá trị “Nhẹ - Khỏe - Bền”, hứa hẹn là bạn đồng hành mới của thế hệ trẻ “Làm chất - Cân tất”.', '<p>C&ocirc;ng nghệ ph&aacute;t triển tạo n&ecirc;n những thay đổi t&iacute;ch cực ở mọi kh&iacute;a cạnh, trong đ&oacute; c&oacute; xu hướng l&agrave;m việc hiện đại. Việc d&agrave;nh 8 tiếng/ng&agrave;y ở văn ph&ograve;ng kh&ocirc;ng c&ograve;n l&agrave; lựa chọn h&agrave;ng đầu của thế hệ lao động trẻ khi họ mong muốn hướng đến lối sống tự do hơn để khơi gợi cảm hứng s&aacute;ng tạo. Xu hướng n&agrave;y cũng được phản &aacute;nh r&otilde; trong một cuộc khảo s&aacute;t của&nbsp;<em>Eden Workplace</em>, khi khoảng 15% nh&acirc;n vi&ecirc;n được hỏi ưa chuộng l&agrave;m việc từ xa, 62% th&iacute;ch m&ocirc; h&igrave;nh &ldquo;hybrid working&rdquo;.</p>\r\n\r\n<p>Khi &yacute; tưởng s&aacute;ng tạo lu&ocirc;n được y&ecirc;u cầu phải vượt mọi khu&ocirc;n khổ, lực lượng lao động trẻ cũng cần ph&aacute; vỡ kh&ocirc;ng gian t&ugrave; t&uacute;ng của văn ph&ograve;ng để h&ograve;a m&igrave;nh v&agrave;o những m&ocirc;i trường năng động, rộng mở hơn. V&igrave; vậy, việc sở hữu bạn đồng h&agrave;nh với c&ocirc;ng năng mạnh mẽ, đ&aacute;p ứng đa dạng trải nghiệm đồ họa phức tạp nhưng vẫn đảm bảo sự gọn nhẹ, di động, t&iacute;nh thời trang lu&ocirc;n l&agrave; mong muốn thế hệ trẻ &ldquo;L&agrave;m chất - C&acirc;n tất&rdquo; hướng đến.</p>\r\n', 26, 6, 0, '2023-04-23 05:37:48', 4, 0),
(38, 'Cáp quang APG gặp sự cố mới, thời gian khắc phục kéo dài', '/uploads/tech2761281606592400.jpg', 'Ngoài việc thời gian sửa nhánh S6 bị lùi, tuyến cáp quang biển APG còn phát sinh lỗi mới trên nhánh S7 gần trạm cập bờ Đà Nẵng', '<p>Th&ocirc;ng tin cập nhật về kế hoạch sửa chữa, khắc phục sự cố tr&ecirc;n c&aacute;c tuyến c&aacute;p quang biển quốc tế m&agrave; c&aacute;c nh&agrave; mạng Việt Nam sử dụng vừa được đại diện một doanh nghiệp cung cấp dịch vụ Internet (ISP) chia sẻ với&nbsp;<em>VietNamNet&nbsp;</em>v&agrave;o tối ng&agrave;y 21/4.</p>\r\n\r\n<p>Theo đ&oacute;, với tuyến c&aacute;p quang biển Asia Pacific Gateway (APG), thời gian dự kiến sửa chữa, khắc phục sự cố tr&ecirc;n nh&aacute;nh S6 hướng kết nối đi Hong Kong, Trung Quốc đ&atilde; bị l&ugrave;i sang gần cuối th&aacute;ng 4.</p>\r\n\r\n<p>Trong khi đ&oacute;, mặc d&ugrave; lỗi tr&ecirc;n nh&aacute;nh S9 hướng kết nối đi Singapore v&agrave; Nhật Bản của tuyến APG đ&atilde; được sửa xong v&agrave;o ng&agrave;y 27/3, song dung lượng tr&ecirc;n c&aacute;p nh&aacute;nh n&agrave;y chưa thể kh&ocirc;i phục do ph&aacute;t hiện sự cố mới tr&ecirc;n nh&aacute;nh S7. &ldquo;Đ&acirc;y l&agrave; ph&acirc;n đoạn c&aacute;p APG rẽ nh&aacute;nh v&agrave;o Việt Nam, với điểm cập bờ tại Đ&agrave; Nẵng. Hiện chưa c&oacute; th&ocirc;ng tin về kế hoạch khắc phục sự cố mới n&agrave;y&rdquo;, đại diện ISP cho hay.</p>\r\n', 27, 6, 0, '2023-04-23 05:39:02', 5, 0),
(39, 'Mối liên hệ của Thần Sấm và Phượng hoàng', '/uploads/giaitri761404483183600.jpg', 'Trong “Avengers Assemble: Omega #1”, Thor bộc phát sức mạnh mới có liên quan tới Phoenix Force. Mối quan hệ kỳ lạ này từng được hé lộ trong một tập truyện trước đó.', '<p>Mới đ&acirc;y,&nbsp;<em>Avengers Assemble: Omega #</em><em>1</em>&nbsp;ch&iacute;nh thức được ra mắt. Tập truyện chứng kiến ​​c&aacute;c si&ecirc;u anh h&ugrave;ng mạnh nhất Tr&aacute;i Đất tập hợp cho trận chiến cuối c&ugrave;ng chống lại Doom Supreme, Mephisto v&agrave; Multiversal Masters of Evil.</p>\r\n\r\n<h3><strong>Thor bộc ph&aacute;t sức mạnh</strong></h3>\r\n\r\n<p>Theo&nbsp;<em>Cbr</em>, một con đập khổng lồ ngăn chặn sức mạnh b&oacute;ng tối bị vỡ tung trong trận chiến, đe dọa x&oacute;a sạch kh&ocirc;ng gian v&agrave; thời gian. Để ngăn chặn n&oacute;, Thor đ&atilde; chạy đến hỗ trợ Echo, Old Man Phoenix v&agrave; Firehair, vật chủ con người đầu ti&ecirc;n của Phoenix Force (Lực lượng Phượng ho&agrave;ng).</p>\r\n\r\n<p>&quot;Ngọn lửa đ&atilde; len lỏi trong cơ thể từ ng&agrave;y ta ra đời! Nh&acirc;n danh mẹ vĩ đại! Ngọn lửa của Mighty Thor! Con trai của Thunderbird!&quot;, anh h&eacute;t lớn trước khi triệu hồi quyền năng.</p>\r\n\r\n<p>Sau đ&oacute;, đột nhi&ecirc;n sức mạnh từ ngọn lửa Phượng ho&agrave;ng, bao tr&ugrave;m cơ thể của anh v&agrave; cả chiếc b&uacute;a Mjolnir. Đ&ograve;n đ&aacute;nh khủng khiếp n&agrave;y đ&atilde; th&agrave;nh c&ocirc;ng ngăn chặn sức mạnh b&oacute;ng tối.</p>\r\n', 30, 6, 0, '2023-04-23 05:41:04', 1, 0),
(40, 'Jonathan Majors kiếm 20 triệu USD nhờ \'Avengers 5\'', '/uploads/giaitri1761615091753300.jpg', 'Jonathan Majors nhận thù lao lên tới 20 triệu USD để đóng siêu ác nhân Kang trong bom tấn \"Avengers: The Kang Dynasty\".', '<p><em>New York Post</em>&nbsp;đưa tin ng&ocirc;i sao sinh năm 1989 đ&atilde; k&yacute; hợp đồng để g&oacute;p mặt trong 2 bom tấn&nbsp;<em>Avengers: The Kang Dynasty&nbsp;</em>v&agrave;&nbsp;<em>Avengers: Secret Wars</em>. Theo đ&oacute;, Jonathan Majors nhận khoản th&ugrave; lao khoảng&nbsp;20 triệu USD&nbsp;cho vai si&ecirc;u &aacute;c nh&acirc;n Kang trong loạt phim&nbsp;<em>Avengers</em>.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, tương lai của Jonathan Majors trong Vũ trụ Điện ảnh Marvel chưa thể chắc chắn sau c&aacute;o buộc h&agrave;nh hung bạn g&aacute;i hồi th&aacute;ng 3 vừa qua. Cả Disney v&agrave; Marvel Studios đều từ chối l&ecirc;n tiếng về vụ việc.</p>\r\n\r\n<p>Nguồn tin th&acirc;n cận tiết lộ với&nbsp;<em>New</em><em>&nbsp;York Post</em>&nbsp;l&yacute; do Disney chưa dừng hợp t&aacute;c với Jonathan Majors d&ugrave; anh vướng b&ecirc; bối v&agrave; bị nhiều nh&atilde;n h&agrave;ng quay lưng thời gian qua l&agrave; sợ anh kiện ngược h&atilde;ng phim. Lựa chọn Majors cho vai Kang the Conqueror l&agrave; khoản đầu tư lớn nhất Disney từng thực hiện.</p>\r\n', 30, 6, 0, '2023-04-23 05:44:35', 3, 0),
(41, 'Charlie Puth lần đầu xuất hiện cùng bạn gái Brooke Sansone', '/uploads/giaitri2761731280773200.jpg', 'Charlie Puth và bạn gái Brooke Sansone lần đầu xuất hiện cùng nhau trên thảm đỏ sự kiện Grammy 2023.', '<p><em>People</em>&nbsp;đưa tin giọng ca&nbsp;<em>Light Switch</em>&nbsp;xuất hiện c&ocirc;ng khai c&ugrave;ng bạn g&aacute;i khi tham dự buổi tiệc tiền lễ trao giải Grammy lần thứ 65 tại Los Angeles (Mỹ). Trước đ&oacute;, Charlie Puth x&aacute;c nhận hẹn h&ograve; c&ugrave;ng Brooke Sansone hồi đầu th&aacute;ng 12/2022.</p>\r\n\r\n<p>Tr&ecirc;n thảm đỏ, Puth xuất hiện với blazer trắng, kết hợp quần c&oacute; họa tiết hoa hồng đỏ. Giọng ca&nbsp;<em>Attention</em>&nbsp;ho&agrave;n thiện bộ đồ với chiếc v&ograve;ng cổ đ&iacute;nh nhiều loại đ&aacute; qu&yacute;. B&ecirc;n cạnh đ&oacute;, Sansone lựa chọn bộ c&aacute;nh t&ocirc;ng đen gợi cảm.</p>\r\n', 29, 6, 0, '2023-04-23 05:46:31', 1, 0),
(42, 'Cuộc trò chuyện giữa hai bố con gây sốt ở Trung Quốc', '/uploads/doisong762197682064800.jpg', 'Clip bé gái 6 tuổi “chất vấn” bố không sinh ra mình vẫn được làm cha cùng lời giải thích hồn nhiên của em gây thích thú trên mạng xã hội Trung Quốc.', '<p>Đoạn video ghi lại cảnh b&eacute; g&aacute;i (kh&ocirc;ng được tiết lộ t&ecirc;n) đến từ tỉnh Giang T&ocirc;, miền Đ&ocirc;ng Trung Quốc &ldquo;chất vấn&rdquo; bố trở th&agrave;nh cha của em như thế n&agrave;o đang lan truyền ch&oacute;ng mặt tr&ecirc;n mạng x&atilde; hội Trung Quốc đại lục,&nbsp;<em>Qingdao Traffic Radio</em>&nbsp;đưa tin.</p>\r\n\r\n<p>&ldquo;V&igrave; sao con lại l&agrave; con của bố? Bố kh&ocirc;ng sinh ra con m&agrave;&rdquo;, c&ocirc; con g&aacute;i hỏi.</p>\r\n\r\n<p>&ldquo;Con biết l&yacute; do đấy&rdquo;, c&ocirc; b&eacute; tiếp tục.</p>\r\n', 36, 6, 0, '2023-04-23 05:54:18', 4, 0),
(43, 'Bất lực \'cai nghiện\' TikTok cho con', '/uploads/doisong1762323059465600.jpg', 'Không ít phụ huynh thừa nhận bản thân cũng bị \"cuốn\" khi lướt TikTok, gặp nhiều khó khăn trong việc hạn chế con xem video có nội dung nhảm nhí, độc hại.', '<p>C&aacute;ch đ&acirc;y hơn nửa năm, Thanh Nga (27 tuổi, nh&acirc;n vi&ecirc;n văn ph&ograve;ng tại TP.HCM) đ&atilde; c&oacute; khoảng thời gian vật lộn để &quot;cai nghiện&quot; điện thoại cho con trai gần 3 tuổi, khi cậu b&eacute; li&ecirc;n tục quấy kh&oacute;c đ&ograve;i xem clip TikTok, YouTube.</p>\r\n\r\n<p>&ldquo;Vấn đề xuất ph&aacute;t từ sai lầm của t&ocirc;i&rdquo;, chị n&oacute;i với&nbsp;<em>Zing</em>. Khi b&eacute; hơn một tuổi, Thanh Nga bắt đầu cho con xem c&aacute;c clip tr&ecirc;n YouTube, sau đ&oacute; l&agrave; TikTok, để rảnh tay khi một m&igrave;nh nấu ăn hay l&agrave;m việc nh&agrave;.</p>\r\n\r\n<p>&ldquo;L&uacute;c đ&oacute;, chỉ cần c&oacute; điện thoại trước mặt, b&eacute; ngồi im để xem cả tiếng. Khi đ&oacute;n con từ nh&agrave; trẻ về v&agrave;o buổi chiều, t&ocirc;i sẽ để b&eacute; ngồi xem khoảng 1 tiếng trong l&uacute;c nấu nướng, v&agrave; khi cho con ăn. Bản th&acirc;n t&ocirc;i cũng rất th&iacute;ch lướt video vui nhộn tr&ecirc;n TikTok n&ecirc;n kh&ocirc;ng nghĩ c&oacute; vấn đề g&igrave;&rdquo;, chị kể.</p>\r\n', 36, 6, 0, '2023-04-23 05:56:23', 2, 0),
(44, 'Gen Z nợ nần chồng chất', '/uploads/doisong2762614031686800.jpg', 'Thiếu tiền chi tiêu, đông thanh niên trẻ tuổi phải sử dụng đến thẻ tín dụng để trang trải trong \"cơn bão\" lạm phát. Song, việc chậm thanh toán khiến họ ôm về khoản nợ lớn.', '<p>Tốc độ nợ t&iacute;n dụng của nh&oacute;m Gen Z tại Mỹ đang tăng nhanh nhất so với những thế hệ kh&aacute;c,&nbsp;<em>Bloomberg</em>&nbsp;nhận định.</p>\r\n\r\n<p>Dịch bệnh, t&igrave;nh h&igrave;nh kinh tế biến động v&agrave; lạm ph&aacute;t dai dẳng đ&atilde; khiến qu&aacute; tr&igrave;nh chuyển sang tuổi trưởng th&agrave;nh của nh&oacute;m người trẻ tuổi kh&ocirc;ng dễ d&agrave;ng. Ngược lại, ch&uacute;ng chứa đầy trắc trở. Hiện tại, nhiều thanh ni&ecirc;n ở xứ cờ hoa mắc nợ v&agrave;o thời điểm quan trọng trong qu&aacute; tr&igrave;nh ph&aacute;t triển t&agrave;i ch&iacute;nh của họ.</p>\r\n\r\n<p>Theo b&aacute;o c&aacute;o mới từ Credit Karma, c&aacute;c thanh ni&ecirc;n Gen Z c&oacute; khoản nợ t&iacute;n dụng trung b&igrave;nh l&ecirc;n tới&nbsp;16.283 USD&nbsp;trong qu&yacute; cuối c&ugrave;ng của năm ngo&aacute;i, tăng 3,1% so với qu&yacute; trước đ&oacute;. Số nợ ở mức thấp nhất đối với những người trẻ tuổi l&agrave;&nbsp;2.781 USD.</p>\r\n', 33, 6, 0, '2023-04-23 06:01:14', 2, 0),
(45, 'Hà Nội lý giải nguyên nhân số ca mắc Covid-19 tăng nhẹ', '/uploads/xahoi762696574329800.jpg', 'Theo Sở Y tế Hà Nội, Covid-19 là loại bệnh họ cúm lây truyền qua đường hô hấp và thường diễn biến tăng lên theo mùa, đặc biệt là dễ lây lan trong thời tiết nồm, ẩm như hiện nay.', '<p>Th&ocirc;ng tin tr&ecirc;n được Ph&oacute; gi&aacute;m đốc Sở Y tế&nbsp;<a href=\"https://zingnews.vn/tieu-diem/ha-noi.html\">H&agrave; Nội</a>&nbsp;Vũ Cao Cương cho biết ng&agrave;y 13/4. Theo &ocirc;ng Cương, những ng&agrave;y qua, tr&ecirc;n nhiều diễn đ&agrave;n, phụ huynh x&ocirc;n xao về việc tăng số ca nhiễm Covid-19, c&aacute;c trường ở H&agrave; Nội t&iacute;nh phương &aacute;n học online.</p>\r\n\r\n<p>&quot;Tuy nhi&ecirc;n, th&agrave;nh phố khẳng định đ&acirc;y l&agrave; th&ocirc;ng tin kh&ocirc;ng ch&iacute;nh x&aacute;c&quot;, &ocirc;ng Cương cho biết.</p>\r\n\r\n<p>Theo Ph&oacute; gi&aacute;m đốc Sở Y tế H&agrave; Nội, theo ghi nhận từ đầu năm đến nay, trong số 661 ca mắc Covid-19 c&oacute; 131 trẻ em, nằm rải r&aacute;c tr&ecirc;n địa b&agrave;n to&agrave;n th&agrave;nh phố. Tỷ lệ n&agrave;y tương đồng với giai đoạn trước v&agrave; thấp hơn so với tỷ lệ nhiễm c&uacute;m m&ugrave;a.</p>\r\n\r\n<p>Đồng thời, tổng số bệnh nh&acirc;n kh&aacute;m v&agrave; điều trị Covid-19 từ đầu năm đến nay tại c&aacute;c bệnh viện của H&agrave; Nội l&agrave; 303 người. Hiện nay, tổng số bệnh nh&acirc;n cần thở oxy gọng k&iacute;nh l&agrave; 10 bệnh nh&acirc;n.</p>\r\n\r\n<p>Tất cả bệnh nh&acirc;n n&agrave;y l&agrave; người mắc bệnh nền như cao huyết &aacute;p, tiểu đường, tim mạch, người cao tuổi&hellip; H&agrave; Nội chưa ghi nhận trường hợp bệnh nh&acirc;n phải thở m&aacute;y hay bệnh nh&acirc;n tử vong do Covid-19.</p>\r\n\r\n<p>Theo &ocirc;ng Cương, từ ng&agrave;y 1/4 đến nay số mắc Covid-19 tr&ecirc;n địa b&agrave;n H&agrave; Nội c&oacute; xu hướng tăng nhẹ do đ&acirc;y l&agrave; loại bệnh họ c&uacute;m l&acirc;y truyền qua đường h&ocirc; hấp v&agrave; thường diễn biến tăng l&ecirc;n theo m&ugrave;a, đặc biệt l&agrave; dễ l&acirc;y lan trong thời tiết nồm, ẩm như hiện nay.</p>\r\n', 45, 6, 0, '2023-04-23 06:02:37', 5, 0),
(46, '18 học sinh lớp 5 nhập viện sau khi uống trà sữa, ăn trái cây lắc', '/uploads/xahoi2762786720647600.jpg', 'Sau buổi liên hoan tại lớp, 18 em học sinh trường Tiểu học Trần Hưng Đạo (thị xã Điện Bàn, Quảng Nam) có biểu hiện đau đầu, nôn nên được đưa đi cấp cứu.', '<p>S&aacute;ng 31/3, &ocirc;ng Nguyễn Văn Hiến, Gi&aacute;m đốc Trung t&acirc;m y tế thị x&atilde; Điện B&agrave;n, Quảng nam&nbsp;cho biết 18 em học sinh lớp 5 trường Tiểu học Trần Hưng Đạo, x&atilde; Điện Phong đang được điều trị, theo d&otilde;i nghi do ngộ độc thức ăn.</p>\r\n\r\n<p>Khoảng 15h30 ng&agrave;y 30/3, gi&aacute;o vi&ecirc;n trường Tiểu học Trần Hưng Đạo ph&aacute;t hiện ch&aacute;u N.N.N.T. (học sinh lớp 5C) c&oacute; triệu chứng đau đầu, n&ocirc;n mửa k&egrave;m theo đau bụng.</p>\r\n\r\n<p>Sau đ&oacute;, 17 học sinh c&ugrave;ng lớp kh&aacute;c đều c&oacute; triệu chứng tương tự ch&aacute;u T. Nh&agrave; trường đ&atilde; đưa 18 học sinh n&agrave;y cấp cứu tại Bệnh viện Đa khoa khu vực Điện B&agrave;n.</p>\r\n', 45, 6, 0, '2023-04-23 06:04:07', 1, 0),
(47, 'Người phụ nữ nước ngoài tử vong sau khi phẫu thuật thẩm mỹ ở TP.HCM', '/uploads/suckhoe762878181893500.jpg', 'Sau khi phẫu thuật thẩm mỹ tại TP.HCM, vài ngày sau, người phụ nữ 45 tuổi đột ngột khó thở, không nói chuyện được, phải đi cấp cứu nhưng không qua khỏi.', '<p>S&aacute;ng 18/4, Sở Y tế TP.HCM đưa ra th&ocirc;ng b&aacute;o về vụ việc một người phụ nữ Đ&agrave;i Loan (Trung Quốc) tử vong sau khi phẫu thuật thẩm mỹ.</p>\r\n\r\n<p>Cụ thể, ng&agrave;y 10/4, đơn vị n&agrave;y tiếp nhận th&ocirc;ng tin từ Cơ quan Cảnh s&aacute;t điều tra, C&ocirc;ng an tỉnh Đồng Nai, về b&agrave; L.S.B. (45 tuổi) tử vong khi đưa đến cấp cứu Bệnh viện Đa khoa Thống Nhất (Đồng Nai).</p>\r\n\r\n<p>Trước đ&oacute;, b&agrave; B. đ&atilde; phẫu thuật tại Bệnh viện Chuy&ecirc;n khoa Phẫu thuật Thẩm mỹ Korean Star - Sao H&agrave;n c&oacute; địa chỉ tại 781/C9 L&ecirc; Hồng Phong, phường 12, quận 10, TP.HCM.</p>\r\n\r\n<p>Ngay sau đ&oacute;, Thanh tra Sở Y tế đ&atilde; phối hợp với Ph&ograve;ng An ninh ch&iacute;nh trị nội bộ - C&ocirc;ng an TP.HCM v&agrave; Cơ quan Cảnh s&aacute;t điều tra, C&ocirc;ng an tỉnh Đồng Nai, đến cơ sở tr&ecirc;n để x&aacute;c minh nội dung vụ việc. Kết quả điều tra ban đầu ghi nhận ng&agrave;y 1/4, b&agrave; L.S.B. đến kh&aacute;m tại Bệnh viện Chuy&ecirc;n khoa Phẫu thuật Thẩm mỹ Korean Star - Sao H&agrave;n với địa chỉ như tr&ecirc;n.</p>\r\n\r\n<p>Ng&agrave;y 2/4, b&agrave; B. nhập viện v&agrave; phẫu thuật thẩm mỹ. Sau đ&oacute;, ng&agrave;y 3/4, bệnh nh&acirc;n được xuất viện v&agrave; trở về tỉnh Đồng Nai. Đến trưa 7/4, b&agrave; B. thấy kh&oacute; thở, kh&ocirc;ng n&oacute;i chuyện được nhưng vẫn c&ograve;n tỉnh t&aacute;o, n&ecirc;n người nh&agrave; đưa đi cấp cứu tại Bệnh viện Đa khoa Thống Nhất v&agrave; tử vong v&agrave;o ng&agrave;y 9/4.</p>\r\n\r\n<p>Sở Y tế TP.HCM đ&atilde; c&oacute; văn bản b&aacute;o c&aacute;o Thanh tra Bộ Y tế v&agrave; Cục quản l&yacute; Kh&aacute;m, chữa bệnh về trường hợp tr&ecirc;n. Sở Y tế cũng phối hợp với cơ quan c&ocirc;ng an tiến h&agrave;nh x&aacute;c minh, l&agrave;m r&otilde; c&aacute;c nội dung li&ecirc;n quan đến việc điều trị cho người bệnh L.S.B., tiến h&agrave;nh xử l&yacute; theo quy định đối với c&aacute;c c&aacute; nh&acirc;n, tổ chức c&oacute; li&ecirc;n quan.</p>\r\n\r\n<p>Hồ sơ bệnh &aacute;n của b&agrave; L.S.B. đang được C&ocirc;ng an tỉnh Đồng Nai thụ l&yacute; giải quyết theo thẩm quyền.</p>\r\n', 49, 6, 0, '2023-04-23 06:05:38', 1, 0),
(48, '7 loại trái cây nên ăn vào mùa hè để đẹp da và khoẻ mạnh', '/uploads/suckhoe2762948984164800.jpeg', 'Mùa nắng nóng khiến làn da thường mất nước và đổ mồ hôi. Ăn các loại trái cây như dưa hấu, dâu tây, kiwi hay đu đủ sẽ cung cấp nước và chất dinh dưỡng cho làn da.', '<p>Theo<em>&nbsp;Food NDTV</em>, nhiệt độ tăng cao, thời tiết nắng n&oacute;ng của m&ugrave;a h&egrave; c&oacute; thể g&acirc;y ra c&aacute;c vấn đề về da như đổ nhiều mồ h&ocirc;i hay k&iacute;ch ứng. Mặc d&ugrave; c&oacute; nhiều c&aacute;ch chăm s&oacute;c da v&agrave; c&aacute;c sản phẩm b&ocirc;i ngo&agrave;i da, chế độ ăn uống hợp l&yacute; gi&uacute;p &iacute;ch rất nhiều trong việc giữ l&agrave;n da khỏe mạnh.</p>\r\n\r\n<p>Một số loại tr&aacute;i c&acirc;y c&oacute; lợi cho việc giải nhiệt từ b&ecirc;n trong, từ đ&oacute; ngăn ngừa c&aacute;c vấn đề về da. Ngo&agrave;i ra, ch&uacute;ng c&ograve;n gi&uacute;p hydrat h&oacute;a v&agrave; bổ sung c&aacute;c chất dinh dưỡng bị mất trong m&ugrave;a h&egrave;, nhờ vậy l&agrave;n da được duy tr&igrave; sự mềm mại, s&aacute;ng khỏe từ b&ecirc;n trong.</p>\r\n\r\n<p>Dưới đ&acirc;y l&agrave; 7 loại tr&aacute;i c&acirc;y bạn n&ecirc;n ăn để c&oacute; l&agrave;n da khỏe mạnh v&agrave; rạng rỡ trong m&ugrave;a h&egrave;:</p>\r\n\r\n<h3>Dưa hấu</h3>\r\n\r\n<p>V&agrave;o m&ugrave;a h&egrave;, bạn c&agrave;ng uống nhiều nước th&igrave; c&agrave;ng tốt cho l&agrave;n da. Dưa hấu l&agrave; một trong những loại tr&aacute;i c&acirc;y chứa tới 94% nước. N&oacute; giữ cho l&agrave;n da dẻo dai v&agrave; đ&agrave;n hồi, đồng thời bổ sung th&ecirc;m yếu tố l&agrave;m m&aacute;t cho chế độ ăn uống m&ugrave;a h&egrave;.</p>\r\n\r\n<p>Ngo&agrave;i ra, loại tr&aacute;i c&acirc;y n&agrave;y cũng cung cấp sự bảo vệ tự nhi&ecirc;n chống lại &aacute;nh nắng mặt trời v&agrave; bức xạ tia cực t&iacute;m c&oacute; hại.</p>\r\n\r\n<h3>Đu đủ</h3>\r\n\r\n<p>Đu đủ chứa nhiều nước v&agrave; c&aacute;c chất dinh dưỡng tốt cho da như vitamin C. Theo tiến sĩ Somodyuti Chandra, b&aacute;c sĩ da liễu tại Bệnh viện Medica Superspecialty (Ấn Độ), c&aacute;c loại tr&aacute;i c&acirc;y như đu đủ chứa nhiều Vitamin C, gi&uacute;p ngăn ngừa mụn vi&ecirc;m v&agrave; được t&igrave;m thấy trong nhiều serum chăm s&oacute;c da.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, vitamin C được biết đến như một chất chống oxy h&oacute;a mạnh mẽ, th&uacute;c đẩy qu&aacute; tr&igrave;nh sản xuất collagen trong da.</p>\r\n\r\n<h3>Quả xo&agrave;i</h3>\r\n\r\n<p>Xo&agrave;i cũng l&agrave; tr&aacute;i c&acirc;y gi&agrave;u vitamin C v&agrave; vitamin A, gi&uacute;p tăng cường sức khỏe của da cũng như cải thiện qu&aacute; tr&igrave;nh sản xuất collagen. N&oacute; cũng chứa chất chống oxy h&oacute;a, giảm t&igrave;nh trạng mụn trứng c&aacute; t&aacute;i ph&aacute;t v&agrave; c&aacute;c vấn đề về da kh&aacute;c.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, c&aacute;c chuy&ecirc;n gia khuy&ecirc;n bạn kh&ocirc;ng n&ecirc;n ăn qu&aacute; nhiều xo&agrave;i v&igrave; c&oacute; thể g&acirc;y phản t&aacute;c dụng, g&acirc;y ra nhiều mụn hơn.</p>\r\n\r\n<h3>Quả dứa</h3>\r\n\r\n<p>Theo dữ liệu của Bộ N&ocirc;ng nghiệp Mỹ, một khẩu phần dứa 100 gam c&oacute; thể cung cấp tới 79% lượng Vitamin C cần thiết h&agrave;ng ng&agrave;y cho cơ thể. Ngo&agrave;i ra, dứa chứa nhiều bromelain, một loại enzyme chống vi&ecirc;m c&oacute; lợi cho sức khỏe của da v&agrave; t&oacute;c.</p>\r\n', 50, 6, 0, '2023-04-23 06:06:49', 4, 0),
(49, 'Thám hiểm Đông Dương hơn 150 năm trước', '/uploads/books6769110685349400.jpg', 'Qua tác phẩm \"Hành trình thám hiểm Đông Dương\", độc giả hiểu thêm về cội nguồn của dân tộc, những điều còn ít người biết về khu vực Đông Dương và sông Mekong cách đây hơn 150 năm.', '<p>Bằng ng&ograve;i b&uacute;t dung dị v&agrave; kh&uacute;c chiết, t&aacute;c phẩm&nbsp;<em>H&agrave;nh tr&igrave;nh th&aacute;m hiểm Đ&ocirc;ng Dương</em>&nbsp;được v&iacute; như khối sa b&agrave;n chi tiết m&ocirc; phỏng to&agrave;n cảnh lưu vực s&ocirc;ng Mekong, v&ugrave;ng nội địa Đ&ocirc;ng Dương v&agrave; Nam Trung Hoa v&agrave;o nửa sau thế kỷ XIX. Với nhiều kiến thức về lịch sử, phong tục, tập qu&aacute;n n&oacute;i chung, t&aacute;c phẩm được xem l&agrave; nguồn sử liệu quan trọng đối với Việt Nam về giai đoạn thế kỷ 19.</p>\r\n\r\n<p>T&aacute;c phẩm được giới thiệu đến độc giả Việt Nam trong bầu kh&ocirc;ng kh&iacute; &quot;Th&aacute;ng 4 đọc s&aacute;ch&quot; nh&acirc;n Ng&agrave;y S&aacute;ch v&agrave; Văn h&oacute;a đọc Việt Nam lần thứ hai năm 2023. Chương tr&igrave;nh diễn ra v&agrave;o s&aacute;ng ng&agrave;y 20/4, tại C&ocirc;ng trường C&ocirc;ng x&atilde; Paris với sự tham dự của nh&agrave; sưu tầm Vũ H&agrave; Tuệ, bi&ecirc;n tập vi&ecirc;n Đỗ Quốc Đạt Nh&acirc;n, đại sứ văn h&oacute;a đọc L&ecirc; Đăng Khoa...</p>\r\n\r\n<h3><strong>C&aacute;nh cửa để hiểu về d&acirc;n tộc m&igrave;nh</strong></h3>\r\n\r\n<p><em>H&agrave;nh tr&igrave;nh th&aacute;m hiểm Đ&ocirc;ng Dương</em>&nbsp;l&agrave; quyển s&aacute;ch đầu ti&ecirc;n của Francis Garnier được xuất bản tại Việt Nam, với bản dịch của dịch giả Nguyễn Minh. Đ&acirc;y l&agrave; cuốn s&aacute;ch thứ ba trong Tủ s&aacute;ch Đ&ocirc;ng Dương do Đ&ocirc;ng A li&ecirc;n kết với nh&agrave; xuất bản Đại học Sư phạm xuất bản.</p>\r\n\r\n<p>Kh&ocirc;ng chỉ giới hạn trong mảng khảo cứu địa l&yacute;, t&aacute;c phẩm c&ograve;n t&oacute;m tắt những trải nghiệm sống động của đo&agrave;n th&aacute;m hiểm về đời sống văn h&oacute;a của c&aacute;c cộng đồng d&acirc;n cư trong lưu vực s&ocirc;ng Mekong, từ những hỉ nộ &aacute;i ố trong đời sống sinh hoạt thường ng&agrave;y đến c&aacute;c lễ hội d&acirc;n gian bản địa, những tệ đoan của người bản xứ.</p>\r\n\r\n<p>Theo bi&ecirc;n tập vi&ecirc;n Đỗ Quốc Đạt Nh&acirc;n, những quyển s&aacute;ch m&agrave; người Ph&aacute;p để lại l&agrave; những c&aacute;nh cửa để ch&uacute;ng ta biết về d&acirc;n tộc m&igrave;nh. &quot;Th&ocirc;ng qua quyển s&aacute;ch, ch&uacute;ng t&ocirc;i rất hy vọng c&oacute; thể giới thiệu cho bạn đọc những tư liệu, từ đ&oacute; ch&uacute;ng ta sẽ t&igrave;m ra được những kết nối tốt hơn đối với lịch sử d&acirc;n tộc&rdquo;, &ocirc;ng chia sẻ về th&ocirc;ng điệp của quyển s&aacute;ch.</p>\r\n', 15, 11, 0, '2023-04-23 07:49:31', 3, 0),
(50, 'Hiểu về ngành xuất bản qua \'Những con chữ ngoài trang sách\'', '/uploads/books7769381838047400.jpg', 'Ông Trần Đình Ba, tác giả sách \"Những con chữ ngoài trang sách\", nhận định rằng in ấn, xuất bản thời nay đã là thời của khoa học công nghệ; các hoạt động cũng được chuyên môn hóa.', '<p>Trần Đ&igrave;nh Ba l&agrave; t&aacute;c giả đ&atilde; c&oacute; những t&aacute;c phẩm nghi&ecirc;n cứu ấn tượng như&nbsp;<em>Việt &aacute;n - Lần theo trang sử cũ</em>,&nbsp;<em>Đằng sau mặt b&aacute;o - Hồi k&yacute; ch&acirc;n dung b&aacute;o ch&iacute; Việt buổi ban đầu đến 1945</em>.</p>\r\n\r\n<p>Với kinh nghiệm nghi&ecirc;n cứu lịch sử của m&igrave;nh, kết hợp c&ugrave;ng hiểu biết của một người l&agrave;m trong ng&agrave;nh xuất bản (l&agrave; một trong những người l&agrave;m xuất bản ti&ecirc;u biểu trong dịp kỷ niệm 70 năm ng&agrave;nh Xuất bản, in v&agrave; ph&aacute;t h&agrave;nh Việt Nam), &ocirc;ng đ&atilde; thực hiện cuốn s&aacute;ch&nbsp;<em>Những con chữ ngo&agrave;i trang s&aacute;ch</em>, trưng ra những mảnh nhỏ về in ấn, xuất bản v&agrave; ph&aacute;t h&agrave;nh s&aacute;ch từ khi c&oacute; kỹ thuật in chữ rời người Ph&aacute;p du nhập đến 1945.</p>\r\n\r\n<p>Trao đổi với&nbsp;<em>Zing</em>, t&aacute;c giả chia sẻ về qu&aacute; tr&igrave;nh nghi&ecirc;n cứu, thu thập tư liệu, đồng thời cung cấp g&oacute;c nh&igrave;n đối chiếu, so s&aacute;nh xuất bản xưa v&agrave; nay.</p>\r\n\r\n<h3>Nỗi t&ograve; m&ograve; từ khi ngồi tr&ecirc;n ghế nh&agrave; trường</h3>\r\n\r\n<p><strong><em>-&nbsp;</em></strong><strong><em>Điều g&igrave; khiến &ocirc;ng nghi&ecirc;n cứu về s&aacute;ch vở, xuất bản giai đoạn trước 1945?</em></strong></p>\r\n\r\n<p>- L&yacute; do th&igrave; rất nhiều. Trước hết thời gian nửa cuối thế kỷ XIX đến 1945 c&oacute; nhiều t&aacute;c giả, t&aacute;c phẩm hay, nổi tiếng, để lại dấu ấn lớn trong sự ph&aacute;t triển văn học, lịch sử v&agrave; n&oacute;i chung l&agrave; văn h&oacute;a Việt Nam một thời như Trương Vĩnh K&yacute;, Trương Minh K&yacute;, Phạm Quỳnh, Trần Trọng Kim, Nguyễn Văn Vĩnh...</p>\r\n\r\n<p>Ngay từ khi ngồi tr&ecirc;n ghế nh&agrave; trường, ch&uacute;ng ta đ&atilde; được đọc nhiều t&aacute;c phẩm li&ecirc;n quan của Hồ Biểu Ch&aacute;nh, Kh&aacute;i Hưng, Nguyễn Tu&acirc;n, Nguyễn C&ocirc;ng Hoan, Vũ Trọng Phụng&hellip; nhưng số đ&oacute; c&ograve;n qu&aacute; &iacute;t so với kho t&agrave;ng đồ sộ họ để lại.</p>\r\n\r\n<p>Đ&oacute; l&agrave; l&yacute; do th&ocirc;i th&uacute;c t&ocirc;i phải t&igrave;m đọc th&ecirc;m v&agrave; kh&ocirc;ng chỉ ở mảng văn học, m&agrave; những t&aacute;c phẩm lịch sử, địa ch&iacute; của Diệp Văn Kỳ, Đặng Th&uacute;c Li&ecirc;ng, Cao Hải Để, Đ&agrave;o Trinh Nhất, Ng&ocirc; Vi Liễn, Huỳnh Thị Bảo H&ograve;a&hellip;</p>\r\n\r\n<p>Th&ecirc;m nữa, s&aacute;ch vở, hoạt động xuất bản thời gian nửa cuối thế kỷ XIX đến 1945 hiện nay lưu trữ, c&ograve;n lại kh&ocirc;ng đầy đủ. Hoạt động xuất bản thời gian n&agrave;y rất &iacute;t s&aacute;ch viết. Chẳng hạn s&aacute;ch&nbsp;<em>Lịch sử xuất bản s&aacute;ch Việt Nam</em>&nbsp;năm 1996, nội dung d&agrave;nh cho thời gian cuối thế kỷ XIX đến 1945 chỉ hơn 40 trang, kh&ocirc;ng bao qu&aacute;t được diện mạo xuất bản thời gian đ&oacute; v&agrave; chỉ tập trung mảng xuất bản.</p>\r\n', 17, 0, 0, '2023-04-23 07:54:02', 1, 0),
(51, 'Cách nhà văn Ngô Tất Tố dạy con', '/uploads/books8769517860915500.png', 'Từ khi các con còn nhỏ, ông đã dạy đọc thuộc \"Truyện Kiều\" và ca dao thành ngữ. Khi cuộc sống khó khăn, niềm tin và sự lạc quan là bài học lớn nhất ông dành cho các con.', '<p>Tại lễ kỷ niệm 130 năm ng&agrave;y sinh nh&agrave; văn Ng&ocirc; Tất Tố được tổ chức v&agrave;o s&aacute;ng ng&agrave;y 20/4, b&agrave; Ng&ocirc; Thị Thanh Lịch (con g&aacute;i của nh&agrave; văn) đ&atilde; chia sẻ về những kỷ niệm x&uacute;c động với cha. &Ocirc;ng l&agrave; một người lu&ocirc;n dạy con c&aacute;i về sự lạc quan v&agrave; truyền đi t&igrave;nh y&ecirc;u văn chương của m&igrave;nh. Nh&acirc;n dịp n&agrave;y, nhiều nh&agrave; văn, nh&agrave; thơ v&agrave; nh&agrave; nghi&ecirc;n cứu c&oacute; mặt để chia sẻ về những đ&oacute;ng g&oacute;p của Ng&ocirc; Tất Tố cho nền nghệ thuật Việt Nam.</p>\r\n\r\n<h3><strong>Những mảng v&ugrave;ng k&yacute; ức c&ograve;n s&oacute;t lại</strong></h3>\r\n\r\n<p>&Ocirc;ng Ng&ocirc; Tất Tố đ&atilde; dạy c&aacute;c con học thuộc&nbsp;<em>Truyện Kiều</em>&nbsp;v&agrave; nhiều c&acirc;u ca dao tục ngữ từ l&uacute;c c&aacute;c con c&ograve;n b&eacute;. B&agrave; Lịch khi ấy d&ugrave; chưa biết chữ nhưng thấy mọi người đọc, b&agrave; cũng dần ghi nhớ từng c&acirc;u một. Cuộc sống y&ecirc;n b&igrave;nh chẳng được bao l&acirc;u th&igrave; chiến tranh ập đến. Gia đ&igrave;nh &ocirc;ng Ng&ocirc; Tất Tố phải sơ t&aacute;n l&ecirc;n ấp Cầu Đen (hay c&ograve;n gọi l&agrave; Đồi Ch&aacute;y). Nơi đ&acirc;y hiện thuộc th&ocirc;n Sậu, x&atilde; Quang Tiến, huyện T&acirc;n Y&ecirc;n, tỉnh Bắc Giang.</p>\r\n\r\n<p>Xung quanh nơi gia đ&igrave;nh nh&agrave; văn Ng&ocirc; Tất Tố chỉ to&agrave;n rừng n&uacute;i heo h&uacute;t. Một v&agrave;i gia đ&igrave;nh d&acirc;n tộc thiểu số sống c&aacute;ch xa khu vực đ&oacute;. Lương thực hạn hẹp chỉ ăn cơm với muối. Muốn c&oacute; nước uống phải đi xuống con suối dưới ch&acirc;n đồi g&aacute;nh l&ecirc;n, đ&aacute;nh ph&egrave;n cho sạch rồi mới sử dụng, nấu nướng được.</p>\r\n\r\n<p>B&agrave; Lịch kể lại: &quot;Một h&ocirc;m &ocirc;ng v&agrave;o bếp đem theo một quả trứng. Chị t&ocirc;i bảo thầy phải cho nhiều tương để mặn hơn mới ăn được l&acirc;u. Một quả trứng mấy người ăn nhưng ng&agrave;y đ&oacute; kh&ocirc;ng ai nghĩ vậy lại khổ. Sau n&agrave;y mới thấy thương thầy v&agrave; mọi người biết bao&quot;.</p>\r\n', 17, 0, 0, '2023-04-23 07:56:18', 2, 0),
(52, 'Sách là hành trang của mỗi cán bộ, chiến sĩ', '/uploads/books9769638513179000.jpg', 'Đối với Quân đội nhân dân, sách luôn là hành trang của mỗi cán bộ, chiến sĩ để học tập, nghiên cứu phục vụ công tác chuyên môn và góp phần bồi dưỡng tâm hồn, nhân cách.', '<p>Hưởng ứng Ng&agrave;y S&aacute;ch v&agrave; Văn h&oacute;a đọc lần thứ hai, Nh&agrave; xuất bản Qu&acirc;n đội nh&acirc;n d&acirc;n tổ chức gặp mặt cộng t&aacute;c vi&ecirc;n s&aacute;ng 19/4 tại H&agrave; Nội.</p>\r\n\r\n<h3>Phục vụ nghi&ecirc;n cứu, g&oacute;p phần bồi dưỡng t&acirc;m hồn, nh&acirc;n c&aacute;ch người l&iacute;nh</h3>\r\n\r\n<p>Thay mặt l&atilde;nh đạo Tổng cục Ch&iacute;nh trị, trung tướng Nguyễn Văn Gấu - Ủy vi&ecirc;n Trung ương Đảng, Ph&oacute; chủ nhiệm Tổng cục ch&iacute;nh trị Qu&acirc;n đội nh&acirc;n d&acirc;n - cho biết những năm qua, Ng&agrave;y S&aacute;ch v&agrave; Văn h&oacute;a đọc đ&atilde; v&agrave; đang thu h&uacute;t sự quan t&acirc;m của to&agrave;n x&atilde; hội. Đối với Qu&acirc;n đội nh&acirc;n d&acirc;n, s&aacute;ch lu&ocirc;n l&agrave; h&agrave;nh trang của mỗi c&aacute;n bộ, chiến sĩ để học tập, nghi&ecirc;n cứu phục vụ c&ocirc;ng t&aacute;c chuy&ecirc;n m&ocirc;n v&agrave; g&oacute;p phần bồi dưỡng t&acirc;m hồn, nh&acirc;n c&aacute;ch, n&acirc;ng cao đời sống văn h&oacute;a tinh thần.</p>\r\n\r\n<p>Đặc biệt, từ khi c&oacute; quyết định của Thủ tướng Ch&iacute;nh phủ về Ng&agrave;y S&aacute;ch, sau đ&oacute; l&agrave; Ng&agrave;y S&aacute;ch v&agrave; Văn h&oacute;a đọc, c&aacute;c hoạt động s&aacute;ch, b&aacute;o v&agrave; văn h&oacute;a đọc lu&ocirc;n được quan t&acirc;m, đ&aacute;p ứng nhu cầu đời sống văn h&oacute;a tinh thần của c&aacute;n bộ, chiến sĩ v&agrave; g&oacute;p phần ho&agrave;n th&agrave;nh tốt nhiệm vụ ch&iacute;nh trị của cơ quan, đơn vị v&agrave; qu&acirc;n đội.</p>\r\n\r\n<p>Đ&aacute;nh gi&aacute; cao việc Nh&agrave; xuất bản Qu&acirc;n đội Nh&acirc;n d&acirc;n tổ chức gặp mặt cộng t&aacute;c vi&ecirc;n hưởng ứng Ng&agrave;y S&aacute;ch v&agrave; Văn h&oacute;a đọc Việt Nam lần thứ hai năm 2023, l&atilde;nh đạo Tổng cục Ch&iacute;nh trị n&oacute;i đ&acirc;y l&agrave; dịp để Nh&agrave; xuất bản v&agrave; c&aacute;c cơ quan đơn vị trong to&agrave;n qu&acirc;n, c&aacute;c cộng t&aacute;c vi&ecirc;n trong v&agrave; ngo&agrave;i qu&acirc;n đội đ&aacute;nh gi&aacute; những kết quả đạt được v&agrave; tiếp tục c&oacute; những giải ph&aacute;p ph&ugrave; hợp n&acirc;ng cao hơn nữa chất lượng c&ocirc;ng t&aacute;c bi&ecirc;n tập, xuất bản.</p>\r\n', 15, 11, 0, '2023-04-23 07:58:18', 3, 0),
(53, 'Sách là nền tảng tinh thần, khơi dậy khát vọng phát triển đất nước', '/uploads/books10769876634276300.jpg', 'Nhắc lại lời Tổng bí thư Nguyễn Phú Trọng, Phó thủ tướng Trần Lưu Quang khẳng định sách phát huy giá trị văn hóa, sức mạnh con người Việt, khơi dậy khát vọng phát triển đất nước.', '<p>L&acirc;u nay, s&aacute;ch v&agrave; việc khuyến kh&iacute;ch đọc s&aacute;ch lu&ocirc;n được chăm lo, ph&aacute;t triển. Từ năm 2021, Ch&iacute;nh phủ đ&atilde; quyết định chuyển Ng&agrave;y S&aacute;ch Việt Nam th&agrave;nh Ng&agrave;y S&aacute;ch v&agrave; Văn ho&aacute; đọc Việt Nam. Ng&agrave;y 21/4 h&agrave;ng năm đ&atilde; trở th&agrave;nh n&eacute;t đẹp văn h&oacute;a, ng&agrave;y hội của người đọc s&aacute;ch, người l&agrave;m s&aacute;ch.</p>\r\n\r\n<p>Năm nay, lễ khai mạc Ng&agrave;y S&aacute;ch v&agrave; Văn h&oacute;a đọc Việt Nam lần thứ hai diễn ra v&agrave;o 17h ng&agrave;y 21/4, tại th&agrave;nh phố Huế. Chương tr&igrave;nh do Bộ Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng phối hợp với Ban Tuy&ecirc;n gi&aacute;o Trung ương, Bộ Văn h&oacute;a, Thể thao v&agrave; Du lịch, Ủy ban nh&acirc;n d&acirc;n tỉnh Thừa Thi&ecirc;n - Huế v&agrave; Hội Xuất bản Việt Nam tổ chức. Đ&acirc;y l&agrave; sự kiện trọng t&acirc;m, mở ra chuỗi hoạt động khuyến đọc tr&ecirc;n cả nước.</p>\r\n\r\n<h3><strong>T&ocirc;n vinh s&aacute;ch v&agrave; khẳng định vai tr&ograve; của văn h&oacute;a đọc</strong></h3>\r\n\r\n<p>C&aacute;c th&ocirc;ng điệp ch&iacute;nh của Ng&agrave;y S&aacute;ch v&agrave; Văn ho&aacute; đọc năm nay l&agrave; &ldquo;S&aacute;ch: Nhận thức - đổi mới - s&aacute;ng tạo&rdquo;; &ldquo;S&aacute;ch cho t&ocirc;i, s&aacute;ch cho bạn&rdquo;; &ldquo;Thời đại mới đ&ograve;i hỏi phải c&oacute; nhận thức mới, c&aacute;ch l&agrave;m mới&rdquo;.</p>\r\n\r\n<p>Ph&aacute;t biểu khai mạc buổi lễ, &ocirc;ng Nguyễn Mạnh H&ugrave;ng, Ủy vi&ecirc;n Ban chấp h&agrave;nh Trung ương Đảng, Bộ trưởng Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng, nhận định: &ldquo;Đổi mới trong nhận thức, đổi mới trong s&aacute;ng tạo, đổi mới trong phương thức xuất bản, đổi mới trong c&aacute;ch tiếp cận bạn đọc để s&aacute;ch vẫn l&agrave; s&aacute;ch, nhưng đa dạng h&igrave;nh tướng, đến được h&agrave;ng triệu người, để xuất bản ph&aacute;t triển mạnh mẽ hơn theo hướng d&acirc;n tộc h&oacute;a nhưng vẫn phải hiện đại h&oacute;a bằng c&ocirc;ng nghệ số, vẫn phải đại ch&uacute;ng h&oacute;a th&ocirc;ng qua đa nền tảng số&rdquo;.</p>\r\n\r\n<p>Đại diện l&atilde;nh đạo Đảng, Nh&agrave; nước, Ph&oacute; thủ tướng Trần Lưu Quang ghi nhận thời gian qua, ng&agrave;nh xuất bản c&oacute; bước ph&aacute;t triển nhanh, văn h&oacute;a đọc c&oacute; sự tiến bộ, phong tr&agrave;o đọc s&aacute;ch, tặng s&aacute;ch đ&atilde; đến được nhiều nơi tr&ecirc;n cả nước.</p>\r\n', 16, 0, 0, '2023-04-23 08:02:17', 1, 0),
(54, 'Tôi khóc lên khóc xuống khi đọc \'Cây cam ngọt của tôi\'', '/uploads/books11770644999649100.jpg', 'Tôi khóc lên khóc xuống khi đọc từng dòng từng chữ trong cuốn \"Cây cam ngọt của tôi\" (tác giả: José Mauro De Vasconcelos) vì tôi thương Zezé.', '<p>T&ocirc;i nhớ t&ocirc;i của 23 năm về trước, một đứa trẻ phải thường xuy&ecirc;n trải qua cảm gi&aacute;c buồn bực v&agrave; trống trải. Bất kỳ khi n&agrave;o t&ocirc;i thấy bản th&acirc;n bồn chồn, t&ocirc;i đều ngo&aacute;i đầu về ph&iacute;a sau, mong ng&oacute;ng v&agrave; chờ đợi.</p>\r\n\r\n<p>Nhiều l&uacute;c t&ocirc;i tự hỏi m&igrave;nh điều n&agrave;y c&oacute; &yacute; nghĩa g&igrave;. Kh&ocirc;ng biết. Thật sự t&ocirc;i kh&ocirc;ng hề hiểu r&otilde; về điều m&agrave; t&ocirc;i đang l&agrave;m. H&agrave;nh động ngo&aacute;i nh&igrave;n lại ph&iacute;a sau chỉ đơn giản l&agrave; t&ocirc;i vừa hy vọng vừa đợi ai đ&oacute;, đặc biệt l&agrave; người quen, nếu họ c&oacute; đ&oacute;, l&ograve;ng t&ocirc;i chợt ấm l&ecirc;n một tia hy vọng kh&ocirc;ng r&otilde; r&agrave;ng nhưng lại xoa dịu được cảm gi&aacute;c trống trải.</p>\r\n\r\n<p>Với &yacute; thức chưa ho&agrave;n thiện một đứa trẻ, t&ocirc;i đơn giản sợ phải ở một m&igrave;nh, sợ phải l&agrave;m mọi thứ một m&igrave;nh. T&ocirc;i lu&ocirc;n th&iacute;ch c&oacute; ai đ&oacute; chia sẻ mọi hoạt động. Nhưng kh&ocirc;ng c&oacute; ai cả. Những người lớn b&ecirc;n cạnh t&ocirc;i đều rất bận rộn. Bận mưu sinh, bận giải quyết những vấn đề ri&ecirc;ng của bản th&acirc;n.</p>\r\n\r\n<p>D&ugrave; vậy việc kh&ocirc;ng được thường xuy&ecirc;n tr&ograve; chuyện với bố mẹ đ&atilde; để lại một vết nứt trong việc kết nối giữa t&ocirc;i v&agrave; người đ&atilde; gi&aacute;n tiếp cho t&ocirc;i cơ hội được hiện diện v&agrave; ngắm nh&igrave;n cuộc sống đẹp m&agrave; cũng lắm khắc nghiệt n&agrave;y.</p>\r\n', 15, 12, 1, '2023-04-23 08:15:05', 1, 0),
(55, 'Cận cảnh smartphone gập đầu tiên của Google', '/uploads/tech3770920669117700.jpg', 'Đây là smartphone có giá thành đắt đỏ nhất trong dòng flagship Pixel của Google.', '<p>Theo nguồn tin nội bộ chia sẻ với&nbsp;<em>CNBC</em>, Google&nbsp;dự t&iacute;nh ra mắt mẫu Pixel gập đầu ti&ecirc;n trước khi c&ocirc;ng bố d&ograve;ng flagship Pixel 8 v&agrave;o th&aacute;ng 10. Thiết bị n&agrave;y c&oacute; t&ecirc;n m&atilde; l&agrave; &ldquo;Felix&rdquo; v&agrave; được Google cam kết l&agrave; c&oacute; phần bản lề bền bỉ nhất trong số c&aacute;c smartphone gập. Pixel Fold c&ograve;n được trang bị t&iacute;nh năng kh&aacute;ng nước v&agrave; nổi bật nhờ thiết kế nhỏ gọn, bỏ vừa t&uacute;i quần.</p>\r\n\r\n<p>Nhưng đ&acirc;y mới chỉ l&agrave; những tin đồn về Pixel Fold. H&ocirc;m 22/4, nh&agrave; nghi&ecirc;n cứu Android Kuba Wojciechowski đ&atilde; đăng tải một đoạn video quay nguy&ecirc;n mẫu thiết bị gập đầu ti&ecirc;n của Google. Trong video, chiếc smartphone kh&ocirc;ng c&oacute; dấu hiệu đặc trưng của ri&ecirc;ng Google như biểu tượng h&atilde;ng, cụm camera ở ph&iacute;a sau.</p>\r\n\r\n<p>Thay v&agrave;o đ&oacute;, đoạn video chỉ quay phần mặt trước chứa camera selfie, viền b&ecirc;n trong v&agrave; m&agrave;n h&igrave;nh gập được bo tr&ograve;n ở 4 g&oacute;c. Theo&nbsp;<em>The Verge</em>, nguy&ecirc;n mẫu n&agrave;y ho&agrave;n to&agrave;n c&oacute; thể bị nhầm l&agrave; thiết bị gập của Samsung.</p>\r\n', 25, 0, 0, '2023-04-23 08:19:41', 2, 0),
(56, 'Tuchel: Mọi thứ tuột khỏi tay Bayern như cát', '/uploads/sport8771118186780000.jpg', 'HLV Thomas Tuchel bày tỏ sự thất vọng khi Bayern Munich trải qua trận thứ 4 liên tiếp không biết thắng trên mọi đấu trường.', '<p>&quot;Ch&uacute;ng t&ocirc;i tr&ocirc;ng c&oacute; vẻ mệt mỏi&quot;, Tuchel thừa nhận sau thất bại với tỷ số 1-3 trước Mainz tối 22/4 (giờ H&agrave; Nội), &quot;C&aacute;ch ch&uacute;ng t&ocirc;i thi đấu giống như một đội b&oacute;ng đ&atilde; chơi hơn 80 trận m&ugrave;a n&agrave;y&quot;.</p>\r\n\r\n<p>Cựu HLV chelsea tỏ ra lo lắng: &quot;Ch&uacute;ng t&ocirc;i kh&ocirc;ng thể chơi thứ b&oacute;ng đ&aacute; ho&agrave;n hảo. C&aacute;c trận đấu v&agrave; số điểm tuột khỏi tay ch&uacute;ng t&ocirc;i như c&aacute;t vậy&quot;.</p>\r\n\r\n<p>Trong khi đ&oacute;, Gi&aacute;m đốc Điều h&agrave;nh Oliver Kahn khẳng định: &quot;Tuchel l&agrave; người cuối c&ugrave;ng m&agrave; ch&uacute;ng t&ocirc;i thảo luận (về vấn đề của Bayern - PV). &Ocirc;ng ấy đang l&agrave;m mọi c&aacute;ch để gi&uacute;p c&aacute;c ch&agrave;ng trai tiến về ph&iacute;a trước. T&ocirc;i sẽ kh&ocirc;ng từ chức v&agrave; cố gắng c&ugrave;ng tập thể v&ocirc; địch Bundesliga trước khi l&agrave;m lại v&agrave;o m&ugrave;a giải tới&quot;.</p>\r\n\r\n<p>Bayern đ&atilde; h&agrave;nh qu&acirc;n tới s&acirc;n của Mainz với hy vọng gi&agrave;nh 3 điểm để vực dậy tinh thần sau khi bị loại ở tứ kết C1&nbsp;hồi giữa tuần. Song, HLV Tuchel chưa thể mang tới kh&aacute;c biệt kể từ khi l&ecirc;n thay Julian Nagelsmann dẫn dắt &quot;H&ugrave;m x&aacute;m&quot;.</p>\r\n\r\n<p>Mainz cầm b&oacute;ng chỉ 26% nhưng tung ra tới 12 c&uacute; s&uacute;t so với 11 của Bayern. Họ cũng c&oacute; 4 lần s&uacute;t tr&uacute;ng mục ti&ecirc;u, ghi 3 b&agrave;n trong khi &quot;H&ugrave;m x&aacute;m&quot; chỉ c&oacute; 2 pha dứt điểm đưa b&oacute;ng hướng về khung th&agrave;nh. Trận thua trước Mainz k&eacute;o d&agrave;i chuỗi kh&ocirc;ng thắng của Bayern l&ecirc;n 4 trận li&ecirc;n tiếp. CLB xứ Bavaria dưới sự chỉ đạo của cựu HLV Chelsea chỉ thắng 2, h&ograve;a 2 v&agrave; thua 3 trong 7 trận.</p>\r\n', 9, 0, 0, '2023-04-23 08:22:58', 3, 0),
(57, 'Avengers hiện như \'rắn mất đầu\'', '/uploads/giaitri3771249076120200.jpg', 'Infinity Saga của MCU khép lại cùng lời tạm biệt của một số anh hùng biểu tượng. Trong khi, thế hệ Avengers mới lại không đáp ứng được mong muốn của nhiều khán giả.', '<p>Sau khi Infinity Saga của MCU kh&eacute;p lại, kh&aacute;n giả phải n&oacute;i lời tạm biệt với nhiều nh&acirc;n vật mang t&iacute;nh biểu tượng như Iron Man, Captain America,... Đ&acirc;y đều l&agrave; những si&ecirc;u anh h&ugrave;ng c&oacute; c&ocirc;ng đầu trong việc th&agrave;nh lập v&agrave; dẫn dắt nh&oacute;m Avengers.</p>\r\n\r\n<p>Ch&iacute;nh v&igrave; vậy, sự vắng mặt của họ tạo ra lỗ hổng kh&ocirc;ng thể b&ugrave; đắp trong c&aacute;c dự &aacute;n phim Marvel thuộc giai đoạn mới.</p>\r\n\r\n<h3><strong>Khoảng trống từ Cap v&agrave; Iron Man</strong></h3>\r\n\r\n<p>Theo&nbsp;<em>Screen Rant</em>, việc MCU cố gắng ra mắt h&agrave;ng loạt nh&acirc;n vật mới đ&atilde; đẩy ch&iacute;nh những anh h&ugrave;ng trẻ tuổi v&agrave;o thế kh&oacute;. Họ phải đối mặt với th&aacute;ch thức chưa từng c&oacute; trong tiền lệ: thiếu l&atilde;nh đạo đủ tầm. Hay n&oacute;i ch&iacute;nh x&aacute;c hơn, kh&ocirc;ng c&oacute; một c&aacute;i t&ecirc;n đủ sức nặng để dẫn dắt nh&oacute;m Avengers ở thời điểm hiện tại.</p>\r\n\r\n<p>Trước đ&oacute;, xuy&ecirc;n suốt Infinity Saga (bao gồm Giai đoạn 1, 2 v&agrave; 3), Captain America lu&ocirc;n được coi l&agrave; thủ lĩnh của cả đội, cả về chiến lược lẫn tinh thần. L&agrave; một trong những si&ecirc;u anh h&ugrave;ng đầu ti&ecirc;n của nước Mỹ theo nguy&ecirc;n t&aacute;c truyện tranh, Captain America (Steve Rogers) được c&ocirc;ng nhận như một biểu tượng của sự th&ocirc;ng minh, ch&iacute;nh trực v&agrave; đầy l&yacute; tưởng.</p>\r\n\r\n<p>Sau khi chấp nhận tham gia th&iacute; nghiệm, Steve l&agrave; một trong số &ldquo;chuột bạch&rdquo; ti&ecirc;n phong được ti&ecirc;m Huyết thanh Si&ecirc;u chiến binh. Anh sau đ&oacute; được chiếu ph&oacute;ng xạ bằng tia Vita để k&iacute;ch hoạt v&agrave; l&agrave;m ổn định c&aacute;c chất h&oacute;a học trong cơ thể. Từ một người l&iacute;nh c&ograve;m c&otilde;i, nhỏ b&eacute;, anh trở th&agrave;nh một chiến binh biểu tượng của nước Mỹ với thể h&igrave;nh lực lưỡng, cơ bắp cuồn cuộn v&agrave; sức khỏe phi thường.</p>\r\n', 30, 0, 0, '2023-04-23 08:25:09', 3, 0);
INSERT INTO `news` (`id`, `title`, `thumbnail`, `shortdescription`, `content`, `cate_id`, `flag`, `view`, `insert_time`, `user_id`, `status`) VALUES
(58, 'Việt Nam tích cực hợp tác xử lý các thách thức tại lưu vực sông Mekong', '/uploads/xahoi3771430774638200.jpg', '\"Các nước đều khẳng định cam kết mạnh mẽ với việc thực hiện Hiệp định Mekong năm 1995, coi trọng vai trò của Ủy hội sông Mekong quốc tế\", Thứ trưởng Đỗ Hùng Việt nói.', '<p>Nh&acirc;n dịp Thủ tướng Phạm Minh Ch&iacute;nh kết th&uacute;c tốt đẹp chuyến tham dự Hội nghị cấp cao Ủy hội s&ocirc;ng Mekong quốc tế lần thứ 4 (tổ chức tại thủ đ&ocirc; Vientiane của L&agrave;o), Thứ trưởng Bộ Ngoại giao Đỗ H&ugrave;ng Việt đ&atilde; trả lời phỏng vấn về kết quả chuyến c&ocirc;ng t&aacute;c của Thủ tướng.</p>\r\n\r\n<p>- Xin Thứ trưởng cho biết những dấu ấn nổi bật trong chuyến c&ocirc;ng t&aacute;c của Thủ tướng Phạm Minh Ch&iacute;nh tham dự Hội nghị cấp cao Ủy hội s&ocirc;ng Mekong quốc tế lần thứ 4?</p>\r\n\r\n<p>- Hội nghị Cấp cao Ủy hội s&ocirc;ng Mekong diễn ra trong bối cảnh lưu vực con s&ocirc;ng n&agrave;y đang đối mặt với những th&aacute;ch thức to lớn do t&aacute;c động k&eacute;p của biến đổi kh&iacute; hậu v&agrave; việc khai th&aacute;c v&agrave; sử dụng qu&aacute; mức t&agrave;i nguy&ecirc;n nước, t&agrave;i nguy&ecirc;n li&ecirc;n quan; ảnh hưởng trực tiếp đến cuộc sống, sinh kế của người d&acirc;n tr&ecirc;n to&agrave;n lưu vực, đặc biệt l&agrave; ở Đồng bằng s&ocirc;ng Cửu Long của Việt Nam.</p>\r\n\r\n<p>Trong bối cảnh đ&oacute;, việc Thủ tướng Phạm Minh Ch&iacute;nh tham dự hội nghị thể hiện sự quan t&acirc;m v&agrave; quyết t&acirc;m ph&aacute;t huy vai tr&ograve; chủ động, t&iacute;ch cực của Việt Nam trong việc ứng ph&oacute; c&aacute;c th&aacute;ch thức, tăng cường đo&agrave;n kết với c&aacute;c nước trong Ủy hội để quản l&yacute; v&agrave; sử dụng bền vững nguồn nước s&ocirc;ng Mekong, bảo đảm lợi &iacute;ch của c&aacute;c quốc gia trong lưu vực, trong đ&oacute; c&oacute; Việt Nam.</p>\r\n', 46, 0, 0, '2023-04-23 08:28:11', 1, 0),
(59, 'Vừa hết nồm ẩm, miền Bắc bước vào đợt nắng nóng 37-38 độ C', '/uploads/xahoi4771534689990200.jpg', 'Kết thúc đợt nồm ẩm, miền Bắc bước vào giai đoạn oi nóng và khô ráo kéo dài suốt tuần tới. Một số nơi ở Tây Bắc Bộ và Bắc Trung Bộ có thể nắng nóng gay gắt trên 37 độ C.', '<p>Ng&agrave;y 16/4, thời tiết miền Bắc chuyển nắng r&aacute;o sau chuỗi ng&agrave;y mưa ph&ugrave;n, ẩm ướt. Nhiệt độ cao nhất ở H&agrave; Nội ở ngưỡng 29-31 độ C, tăng 3 độ C so với một ng&agrave;y trước. Độ ẩm giảm thấp dưới 70%.</p>\r\n\r\n<p>Trung t&acirc;m Dự b&aacute;o Kh&iacute; tượng Thủy văn Quốc gia cho biết trong c&aacute;c ng&agrave;y 18-22/4, T&acirc;y Bắc Bộ, Bắc Trung Bộ v&agrave; Trung Trung Bộ đ&oacute;n đợt nắng n&oacute;ng mới. Nhiệt độ cao nhất 35-37 độ C, c&oacute; nơi nắng n&oacute;ng gay gắt tr&ecirc;n 38 độ C.</p>\r\n\r\n<p>C&ugrave;ng l&uacute;c, Đ&ocirc;ng Bắc Bộ nắng n&oacute;ng cục bộ v&agrave;o tuần tới. Khu vực duy tr&igrave; t&igrave;nh trạng oi n&oacute;ng, kh&ocirc; r&aacute;o li&ecirc;n tục c&aacute;c ng&agrave;y 17-22/4. Nhiệt độ cao nhất 31-33 độ C, chưa chạm ngưỡng nắng n&oacute;ng.</p>\r\n', 46, 0, 0, '2023-04-23 08:29:55', 1, 0),
(60, 'Những phần ngoại truyện thành công nhờ series gốc', '/uploads/sachhay11589681559928000.jpg', 'Dù chỉ được coi là các tác phẩm sáng tạo thêm dựa trên series chính, có nhiều bộ ngoại truyện đã thành công vượt sự kỳ vọng của người hâm mộ.', '<p>C&aacute;c phần ngoại truyện (hay c&ograve;n gọi l&agrave; spin-off) lu&ocirc;n c&oacute; một sức hấp dẫn ri&ecirc;ng bởi sự mở rộng từ kh&ocirc;ng gian v&agrave; tuyến nh&acirc;n vật trong series gốc. T&aacute;c giả c&oacute; thể lựa chọn nhiều kiểu s&aacute;ng tạo cho ngoại truyện, chẳng hạn: Thay đổi g&oacute;c nh&igrave;n để kể lại c&acirc;u chuyện, viết về nguồn gốc của c&aacute;c nh&acirc;n vật ch&iacute;nh, l&yacute; giải sự h&igrave;nh th&agrave;nh của một thế lực, sức mạnh (thường xuất hiện trong tiểu thuyết viễn tưởng)... Sau đ&acirc;y l&agrave; những phần ngoại truyện được độc giả đ&aacute;nh gi&aacute; cao.</p>\r\n\r\n<h3>1.&nbsp;<em>Grey</em>&nbsp;(EL James)</h3>\r\n\r\n<p><em>Grey</em>&nbsp;l&agrave; phần mở rộng đối với series&nbsp;<em>50 sắc th&aacute;i</em>&nbsp;của nh&agrave; văn người Anh EL James. C&acirc;u chuyện về t&igrave;nh y&ecirc;u v&agrave; nỗi &aacute;m ảnh thể x&aacute;c sẽ được kể qua g&oacute;c nh&igrave;n của nh&acirc;n vật ch&iacute;nh Christian Grey. Grey cảm thấy m&igrave;nh lu&ocirc;n phải sắp đặt mọi thứ trong cuộc sống một c&aacute;ch ho&agrave;n chỉnh. Những thương tổn từ thời thơ ấu đ&atilde; h&igrave;nh th&agrave;nh n&ecirc;n căn ph&ograve;ng Đỏ trong tr&iacute; &oacute;c của ri&ecirc;ng của anh. Grey e ngại c&aacute;nh cửa t&igrave;nh y&ecirc;u sẽ kh&ocirc;ng thể mở ra cho một con người như m&igrave;nh. Thế rồi, Grey đ&atilde; gặp Anastasia Steele, một c&ocirc; g&aacute;i trẻ trung, dịu d&agrave;ng v&agrave; ngọt ng&agrave;o.</p>\r\n\r\n<p>Nhờ việc thay đổi điểm nh&igrave;n, độc giả c&oacute; thể thấy r&otilde; hơn l&yacute; do căn ph&ograve;ng Đỏ được Grey x&acirc;y dựng với đầy những dụng cụ tra tấn. Những mối quan hệ đi&ecirc;n cuồng của Grey trước đ&oacute; cũng h&eacute; lộ nhiều hơn, đặc biệt l&agrave; Elena, người đ&atilde; lạm dụng t&igrave;nh dục Grey. Ch&iacute;nh nỗi &aacute;m ảnh v&agrave; tổn thương từ tuổi thơ khiến Grey lu&ocirc;n sợ rằng bản th&acirc;n sẽ đ&aacute;nh mất Ana. Cuốn s&aacute;ch kh&ocirc;ng chỉ thuật lại c&aacute;c sự kiện như một phi&ecirc;n bản t&oacute;m gọn của series gốc, n&oacute; cho thấy một thế giới t&acirc;m hồn của một người bị &aacute;m ảnh cưỡng chế.</p>\r\n\r\n<h3>2.&nbsp;<em>Stranger Things: Flight of Icarus</em>&nbsp;(Caitlin Schneiderhan)</h3>\r\n\r\n<p>Sau th&agrave;nh c&ocirc;ng của series truyền h&igrave;nh&nbsp;<em>Stranger Things</em>, phần ngoại truyện về nh&acirc;n vật Eddie Munson sẽ được ra mắt người h&acirc;m mộ v&agrave;o th&aacute;ng 10. Trước đ&oacute;, trong series n&agrave;y, kh&aacute;n giả đ&atilde; rất ch&uacute; &yacute; đến Eddie, c&aacute;c chủ đề được thảo luận s&ocirc;i nổi tr&ecirc;n nhiều diễn đ&agrave;n. Dấu ấn của Eddie được khắc họa r&otilde; n&eacute;t hơn mỗi lần anh giao chiến, giai điệu b&agrave;i&nbsp;<em>Master of Puppets&nbsp;</em>của Metallica lại nổi l&ecirc;n. Sự vắng b&oacute;ng của nh&acirc;n vật n&agrave;y v&agrave;o m&ugrave;a cuối c&ugrave;ng khiến mọi người tiếc nuối.</p>\r\n', 15, 0, 0, '2023-05-02 19:45:15', 2, 0),
(64, '5 tác phẩm kinh điển từng bị giới phê bình phản đối', '/uploads/sachhay21835643060272400.jpg', 'Không phải tác phẩm kinh điển nào cũng nhận được nhiều lời khen ngợi ngay từ đầu.', '<p>Trong suốt lịch sử ph&aacute;t triển văn học, đ&atilde; c&oacute; nhiều t&aacute;c phẩm kinh điển được độc giả y&ecirc;u th&iacute;ch nhưng lại bị giới ph&ecirc; b&igrave;nh chỉ tr&iacute;ch kịch liệt. Nhưng thời gian tr&ocirc;i qua, c&aacute;c nh&agrave; ph&ecirc; b&igrave;nh c&oacute; xu hướng đứng về ph&iacute;a những độc giả y&ecirc;u mến ch&uacute;ng ngay từ đầu.</p>\r\n\r\n<p>Trang&nbsp;<em>Big Think</em>&nbsp;đ&atilde; chia sẻ về 5 kiệt t&aacute;c văn học được độc giả y&ecirc;u th&iacute;ch nhưng lại bị c&aacute;c nh&agrave; ph&ecirc; b&igrave;nh chỉ tr&iacute;ch trong khoảng thời gian ch&uacute;ng ra mắt.</p>\r\n', 15, 0, 0, '2023-05-05 16:04:30', 2, 0),
(65, 'AI sẽ giúp con người làm những công việc mà chúng ta không thể làm', '/uploads/sachhay31835775965857800.jpg', 'AI sẽ khiến thế giới trở nên tốt đẹp hơn, đồng thời đem lại giá trị tốt đẹp cho nhiều khía cạnh trong cuộc sống.', '<p>D&ugrave; bạn l&agrave; ai, trong tương lai rất gần, bạn sẽ lu&ocirc;n phải tương t&aacute;c với AI trong hầu hết mọi khoảnh khắc của cuộc sống, thậm ch&iacute; l&agrave; tương t&aacute;c trong v&ocirc; thức. Một ứng dụng n&agrave;o đ&oacute; sẽ đ&aacute;nh thức bạn khi bạn đang thiu thiu ch&igrave;m v&agrave;o giấc ngủ.</p>\r\n\r\n<p>Trong l&uacute;c ăn s&aacute;ng, AI sẽ sắp xếp lịch tr&igrave;nh h&agrave;ng ng&agrave;y, t&igrave;m kiếm con đường &iacute;t tắc nghẽn nhất để bạn đi l&agrave;m, gợi &yacute; những tin tức n&oacute;ng hổi hay những th&ocirc;ng tin mạng x&atilde; hội, ph&ugrave; hợp với sở th&iacute;ch của bạn. Khi bạn muốn soạn thảo email, những thuật to&aacute;n đ&aacute;nh gi&aacute; t&acirc;m l&yacute; sẽ ph&acirc;n t&iacute;ch t&igrave;nh h&igrave;nh của người nhận trong thời điểm đ&oacute;, bao gồm blog, email, những b&igrave;nh luận v&agrave; b&agrave;i đăng, để hỗ trợ gợi &yacute; gi&uacute;p ng&ocirc;n từ của bạn c&oacute;</p>\r\n', 15, 12, 0, '2023-05-05 16:06:43', 2, 0),
(66, 'Sách mê tín dị đoan tràn lan từ ngoài đường đến trên mạng', '/uploads/sachhay41835902649013300.jpg', 'Chỉ cần bỏ vài trăm nghìn, thậm chí vài chục nghìn là ai cũng có thể mua cho mình một cuốn sách mê tín dị đoan.', '<p>Mặc d&ugrave; cơ quan chức năng đ&atilde; quyết liệt v&agrave;o cuộc nhưng t&igrave;nh trạng n&agrave;y vẫn đang diễn ra hết sức s&ocirc;i động. Nguy&ecirc;n nh&acirc;n l&agrave; nhu cầu của người d&acirc;n về thể loại s&aacute;ch n&agrave;y vẫn cao, mức xử phạt với h&agrave;nh vi n&agrave;y chưa đủ sức răn đe.</p>\r\n\r\n<h3><strong>B&aacute;n tr&agrave;n lan</strong></h3>\r\n\r\n<p>L&acirc;u nay tại c&aacute;c cổng ch&ugrave;a lớn, lễ hội thường xuất hiện nhiều người b&agrave;y b&aacute;n c&ocirc;ng khai nhiều loại s&aacute;ch về thế giới v&ocirc; h&igrave;nh. Từ s&aacute;ch b&oacute;i to&aacute;n, tử vi, tướng số, nghi&ecirc;n cứu vận mệnh đến những cuốn s&aacute;ch về vong linh.</p>\r\n\r\n<p>Những tựa s&aacute;ch h&agrave;ng chục năm vẫn vậy như:&nbsp;<em>Tử vi tướng ph&aacute;p trọn đời; B&oacute;i to&agrave;n thư; Đo&aacute;n mộng; Giải mộng; Xem quẻ ch&acirc;n g&agrave;; Số tiền định; S&aacute;ch xem tuổi l&agrave;m nh&agrave; v&agrave; dựng vợ, gả chồng</em>... Hầu hết s&aacute;ch được b&agrave;y b&aacute;n đều l&agrave; s&aacute;ch photocopy hoặc in m&agrave;u sơ s&agrave;i, nhem nhuốc kh&ocirc;ng in t&ecirc;n t&aacute;c giả v&agrave; nơi xuất bản r&otilde; r&agrave;ng. Thậm ch&iacute; nhiều cuốn c&ograve;n viết sai ch&iacute;nh tả nhưng vẫn được rất nhiều người mua.</p>\r\n', 15, 12, 0, '2023-05-05 16:08:49', 2, 0),
(67, 'Càng ít kỳ vọng, càng hạnh phúc', '/uploads/sachhay51836099363103200.jpg', 'Nếu bạn mong một đời sống cao sang và không ngừng theo đuổi vật chất, cho dù là điện thoại di động, quần áo, nhà cửa hay xe hơi, thì bạn sẽ không bao giờ cảm thấy hài lòng.', '<p><img alt=\"\" src=\"http://localhost:8089/userfiles/files/sachhay5.jpg\" style=\"height:500px; width:752px\" /></p>\r\n\r\n<p>Ham muốn l&agrave; v&ocirc; tận. Nhiều người tin rằng c&oacute; thể t&igrave;m thấy hạnh ph&uacute;c trong nh&agrave; cửa, xe hơi, danh tiếng, của cải v&agrave; địa vị, v&igrave; vậy họ bận rộn hết ng&agrave;y n&agrave;y qua ng&agrave;y kh&aacute;c để đuổi theo những thứ như vậy. Nhưng c&oacute; bao nhi&ecirc;u người thực sự hạnh ph&uacute;c sau khi c&oacute; được những thứ họ mong muốn?</p>\r\n\r\n<p>Xưa kia, c&oacute; một quốc vương nọ sống một cuộc đời xa hoa ph&oacute;ng t&uacute;ng. &Ocirc;ng sở hữu nhiều v&agrave;ng bạc ch&acirc;u b&aacute;u v&agrave; lạc th&uacute; nhưng vẫn kh&ocirc;ng thấy hạnh ph&uacute;c. &Ocirc;ng kh&ocirc;ng thể n&agrave;o khiến bản th&acirc;n vui l&ecirc;n n&ecirc;n đ&atilde; gọi th&aacute;i y.</p>\r\n\r\n<p>Sau khi kiểm tra bệnh t&igrave;nh một l&uacute;c l&acirc;u, th&aacute;i y liền t&acirc;u rằng: &ldquo;Dạ, xin cho t&igrave;m người hạnh ph&uacute;c nhất vương quốc. Khi ng&agrave;i mặc chiếc &aacute;o của hắn th&igrave; ng&agrave;i sẽ thấy hạnh ph&uacute;c&rdquo;.</p>\r\n\r\n<p>Quốc vương b&egrave;n cho quần thần t&igrave;m kiếm khắp nơi người hạnh ph&uacute;c nhất vương quốc. Cuối c&ugrave;ng, họ cũng t&igrave;m thấy một người hạnh ph&uacute;c bất biến. Tuy nhi&ecirc;n, tể tướng lại bẩm b&aacute;o với quốc vương rằng kh&ocirc;ng may l&agrave; kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o mang chiếc &aacute;o của hắn trở về.</p>\r\n', 15, 12, 0, '2023-05-05 16:12:06', 2, 0),
(68, 'Những nề nếp sinh hoạt lạ lùng của người Ai Cập cổ đại', '/uploads/sachhay61836237122441400.jpg', 'Nhờ đọc \"Người Ai Cập - Quyền lực và tình yêu\", tôi được đắm chìm vào thế giới Ai Cập cổ đại sinh động, biết thêm về những nề nếp lạ lùng của người cổ đại.', '<p><img alt=\"\" src=\"http://localhost:8089/userfiles/files/sachhay7.jpg\" style=\"height:400px; width:533px\" /></p>\r\n\r\n<p>Ở Phần Lan, người ta n&oacute;i rằng nếu ai đ&oacute; chỉ đọc một cuốn s&aacute;ch trong đời, th&igrave; cuốn s&aacute;ch n&ecirc;n đọc l&agrave;&nbsp;<em>Người Ai Cập - Quyền lực v&agrave; t&igrave;nh y&ecirc;u</em>&nbsp;của Mika Waltari. Vậy, t&aacute;c phẩm 1200 trang n&agrave;y c&oacute; g&igrave; để được t&ocirc;n v&agrave;o h&agrave;ng phải đọc như vậy?</p>\r\n\r\n<p>Một cuốn s&aacute;ch với chủ đề phức tạp v&agrave; nhiều lớp lang. T&igrave;nh y&ecirc;u, t&igrave;nh bạn, tranh đấu quyền lực, người gi&agrave;u v&agrave; kẻ ngh&egrave;o, tuổi thơ, lỗi lầm, phản bội, trả th&ugrave;, nỗi c&ocirc; đơn&hellip; Đ&acirc;y kh&ocirc;ng phải l&agrave; c&acirc;u chuyện đơn giản về c&aacute;i thiện đấu tranh với c&aacute;i &aacute;c; m&agrave; c&ograve;n l&agrave; về c&aacute;i &aacute;c trong mầm thiện, l&yacute; lẽ của c&aacute;i &aacute;c, về những đắng cay chua ch&aacute;t của nh&acirc;n sinh.</p>\r\n\r\n<p>Cốt truyện chặt chẽ với nhiều n&uacute;t thắt bất ngờ, nhiều t&igrave;nh tiết l&agrave;m đau x&eacute; l&ograve;ng. Những biến cố, chia cắt, bất hạnh, đặc biệt những lựa chọn kh&oacute; khăn của nh&acirc;n vật ch&iacute;nh&hellip; sẽ &ldquo;l&agrave;m nội thương&rdquo; ngay cả những độc giả lạnh l&ugrave;ng, cứng cỏi nhất.</p>\r\n\r\n<p>Thế giới cổ đại đầy sinh động được hiện l&ecirc;n qua những n&eacute;t mi&ecirc;u tả t&agrave;i hoa của t&aacute;c giả. Một Thebes ồn &agrave;o, n&aacute;o nhiệt, s&ocirc;ng Nile &ecirc;m đềm hay sa mạc r&aacute;t n&oacute;ng&hellip; T&aacute;c giả đưa người đọc ngược chiều lịch sử v&agrave; ch&igrave;m đắm trong thế giới sống động xa xưa ấy.</p>\r\n\r\n<p>Ta cũng được biết th&ecirc;m về những nề nếp lạ l&ugrave;ng của người cổ đại, như chuyện khoan sọ để chữa bệnh, những bảng đất s&eacute;t họ d&ugrave;ng để ghi ch&eacute;p, c&aacute;ch họ đối đ&atilde;i với n&ocirc; lệ, hay tập qu&aacute;n, trang phục, t&ocirc;n gi&aacute;o&hellip; kh&aacute;c biệt giữa c&aacute;c vương quốc.</p>\r\n\r\n<p>Kh&oacute; ai c&oacute; thể tin được l&agrave; t&aacute;c giả Mika Waltari chưa hề đặt ch&acirc;n tới Ai Cập cho đến khi viết xong cuốn tiểu thuyết n&agrave;y. Một t&aacute;c phẩm d&agrave;y tới 1.200 trang in trong tiếng Việt (800 trang trong tiếng Phần Lan) được &ocirc;ng viết chỉ trong hơn ba th&aacute;ng m&ugrave;a h&egrave; năm 1945 tr&ecirc;n g&aacute;c m&aacute;i ng&ocirc;i nh&agrave; nghỉ của mẹ vợ, dựa tr&ecirc;n những tư liệu sưu tập được từ c&aacute;c thư viện v&agrave; tr&iacute; tưởng tượng phong ph&uacute; của m&igrave;nh.</p>\r\n\r\n<p>Nhật b&aacute;o&nbsp;<em>The New York Times</em>&nbsp;nhận x&eacute;t cuốn s&aacute;ch l&agrave; &ldquo;Một bức tranh to&agrave;n cảnh sống động, th&uacute; vị về thời cổ đại của một người kể chuyện t&agrave;i ba&rdquo;. T&ocirc;i đồng t&igrave;nh với nhận định n&agrave;y.</p>\r\n', 8, 0, 0, '2023-05-05 16:14:24', 2, 0);

